INSERT INTO `movie` (`movie_id`, `movie_name`, `movie_summary`, `movie_pic`, `movie_type`, `date`, `movie_time`, `rate`, `rate_number`) VALUES 
  (1,'红海行动','索马里海域外，中国商船遭遇劫持，船员全数沦为阶下囚。蛟龙突击队沉着应对，潜入商船进行突袭，成功解救全部人质。 返航途中，非洲北部伊维亚共和国发生政变，恐怖组织连同叛军攻入首都，当地华侨面临危险，海军战舰接到上级命令改变航向，前往执行撤侨任务。蛟龙突击队八人，整装待发。 时间紧迫，在“撤侨遇袭可反击，相反则必须避免交火，以免引起外交冲突”的大原则下，海军战舰及蛟龙突击队深入伊维亚，在恶劣的环境之下，借助海陆等多种装备，成功转移等候在码头的中国侨民，并在激烈的遭遇战之后，营救了被恐怖分子追击的中国领事馆巴士。 然而事情尚未完结，就在掩护华侨撤离之际，蛟龙突击队收到中国人质被恐怖分子劫持的消息。众人','//imgwx1.2345.com/dypcimg/img/8/65/sup196226_223x310.jpg',NULL,NULL,'110',4,1),
  (2,'幕后玩家','坐拥数亿财产的钟小年（徐峥 饰），在事业如日中天之时却突遭意外，麻烦不断。在经过一系列的�境后，逢凶化吉的他逐渐实现了自我救赎。','//imgwx1.2345.com/dypcimg/img/1/13/sup39889_223x310.jpg',NULL,NULL,'123',4,1),
  (3,'无问西东','如果提前了解了你所要面对的人生，你是否还会有勇气前来？吴岭澜、沈光耀、王敏佳、陈鹏、张果果，几个年轻人满怀诸多渴望，在四个非同凡响的时空中一路前行。 吴岭澜出发时意气风发，却很快在途中迷失了方向。沈光耀自愿参与了最残酷的战争，他一直在努力去做那些令他害怕，但重要的事。王敏佳最初的错误，只是为了虚荣撒了一个小谎；最初的烦恼，只是在两个优秀的男人中选择一个。但命运，却把她拖入被众人唾骂的深渊。陈鹏把爱情摆在了理想前面，但爱情却没有把他摆在前面。他说，“我有人要照顾”，纵然这意味着与所有人作对，意味着要和她一起被放逐千里。张果果身处尔虞我诈的职场，“赢”是他的习惯。为了赢，他总是见招拆招，先发制人。','//imgwx5.2345.com/dypcimg/img/9/64/sup193253_223x310.jpg',NULL,NULL,'95',4,1),
  (4,'唐人街探案2','秦风（刘昊然饰）接到唐仁（王宝强饰）的邀请来纽约参加其与阿香的婚礼。豪气逼人的唐仁迎接秦风，极尽招摇，岂料婚礼其实是唐仁为巨额奖金而参加的“世界名侦探大赛”，比赛的内容是寻找“唐人街教父”七叔的孙子。受骗的秦风怒极欲走，却被 FBI 探员陈英送来的讯息所吸。','//imgwx3.2345.com/dypcimg/img/1/64/sup194116_223x310.jpg',NULL,NULL,'120',4,1),
  (5,'熊出没・变形记','《熊出没・变形记》为观众展示了一个充满奇光异彩的“微观世界”，光头强和熊大、熊二离奇“变形缩小”，原本生活的世界仿佛变成了“巨人国”，任何普通的事物都成为了他们冒险中的阻碍：如在鱼缸中体验“惊涛骇浪”，在草坪里躲避“重重危机”，餐厅厨房变成了刀山火海般的世界、下水道成为五光十色的小城……与此同时，一个巨大阴谋正在背后悄然展开，熊强三人在不知不觉中陷入一个前所未有的危机','//imgwx2.2345.com/dypcimg/img/3/65/sup196116_223x310.jpg',NULL,NULL,'',4,1),
  (6,'毕业作品','警校学生殷浩和自己的女朋友――电影编导系学生小暖为完成小暖的毕业作品登上了隔离岛拍摄纪录片。殷浩在拍摄纪录片之外，还有自己的小心思，他希望与小暖在岛上进一步巩固恋人关系。二人在岛上偶遇了大商人丁子楚，丁子楚为寻找失踪的女儿娜娜来到岛上却意外丧命，三十年前的大火，让众人都卷入了一场漩涡之中。殷浩发挥警校学生的特长探案追凶，而小暖拍摄的纪录片也成为了殷浩搜寻真相的重要证据。命案在岛上接连发生，小暖也陷入危机，殷浩探明真相后为爱勇闯险境成功救出小暖，二人感情升温的同时，也交出了真正的毕业作品，完成了一次成长的蜕变。','//imgwx1.2345.com/dypcimg/img/b/66/sup198846_223x310.jpg',NULL,NULL,'',4,1),
  (7,'捉妖记2','上一集与胡巴分别后，天荫带着小岚踏上寻父之路，在义薄云天的天师堂堂主云大哥的帮助下，二人得知天荫父亲宋戴天的护妖轨迹；而重回永宁村的胡巴再度被妖王追杀，颠沛流离逃亡时结识大赌徒屠四谷和一只妖怪，三人一起过着相依为命的生活，但又因屠四谷欠下的巨额赌债横生诸多波折。与此同时，江湖盛传小妖王胡巴的重金悬赏令，妖界大军、天师精英、绿林草莽闻风而动，多方势力为抢夺胡巴在清水镇掀起腥风血雨。千钧一发之际，念子心切的天荫和小岚通过天师堂找到胡巴并一起逃离险境。岂料，一场更大的惊天阴谋尾随而至，伺机而动……','//imgwx2.2345.com/dypcimg/img/6/63/sup191464_223x310.jpg',NULL,NULL,'95',4,1),
  (8,'21克拉','刘佳音（迪丽热巴 饰）因与富豪男友分手而破产。为了还债，变卖了自己所有的奢侈品。王继伟（郭京飞 饰）天生爱省，为了省钱无所不用其极。两人因为一场剧场意外相遇，在一系列机缘巧合下合租在一个屋檐之下，过起了荒诞爆笑的流浪生活，“王抠抠”和“刘买买”究竟如何在两种价值观之间化解金钱与爱情的终极问题呢？','//imgwx4.2345.com/dypcimg/img/1/65/sup197968_223x310.jpg',NULL,NULL,'95',4,1),
  (9,'万有引力','这是四个与爱情有关的故事：审计员高远（文章 饰）三年来重复着两地奔忙的工作，在他前往外地送交一份重要文件之时，机场安检员石晓琳（白百何 饰）却将他扣留，耽误了航班的高远大怒，却发现石晓琳对他非常了解……；数学教师王勇（郭涛 饰）与妻子雪莲（张静初 饰）结婚多年，在朋友的启发下决定要个孩子，然而这对爱人的怀孕战役进行的异常艰难……；阿美（莫文蔚 饰）与情人遭遇因信号灯故障引起的车祸，阿美轻伤，而情人重伤昏迷，而这场车祸，似乎由阿美的丈夫云山（戴立忍 饰）一手策划……；跳伞运动员嘉欣（黄幻 饰）因吸毒被跳伞队开除，运动生涯宣告结束，她的男友兼搭档海桥（段博文 饰）决定不惜一切代价挽救她……','//imgwx2.2345.com/dypcimg/img/0/7/sup22809_223x310.jpg',NULL,NULL,'90',4,1),
  (10,'西北风云','本片讲述了女缉毒队长侦破跨境走私贩毒案的故事。在一次追捕行动中，高桥（余男 饰）因与毒贩搏斗致脑部受伤，失去了味觉。不久后，高桥与砂锅粥店老板陆鸿（任达华 饰）因一起车祸，在救人时相识。陆鸿的一碗砂锅粥让高桥尝到了久违的味道，她成了砂锅粥店的常客。他是厨师，也是毒师；她爱破案，也爱吃饭。随着调查的深入，陆鸿的真实身份浮出水面，一心想退隐江湖的他将何去何从？眼看收网在即，一系列杀人案又让高桥陷入了一个巨大的复仇计划中……','//imgwx4.2345.com/dypcimg/img/b/66/sup198696_223x310.jpg',NULL,NULL,'',4,1),
  (11,'勇敢者游戏:决战丛林','影片主要讲述了性格迥异的四人，因为共同的课后留校聚在一起，又因让人“远离世界之路”的“勇敢者的游戏”而产生命运纠葛，共同打破次元壁，经历一场考验勇气智慧，接受成长磨炼的冒险的故事。','//imgwx1.2345.com/dypcimg/img/e/65/sup196028_223x310.jpg',NULL,NULL,'114',4,1),
  (12,'奇葩朵朵','天才理工男黄剑与品学兼优的富二代许子聪为争一个麻省理工学院（MIT）知名物理学教授招录的名额，而成为明争暗斗的竞争对手。黄剑为达到MIT对社团经验的要求，不得不选择加入奇葩各异的淑媛社，也因此与想要挖掘劲爆素材而隐藏身份混入其中的菜鸟记者朱珠相遇。在共同经历各种“奇葩盛事”后，两人逐渐从“冤家路窄”到“爱意萌动”， 展现“学霸与学渣”间奇特的万有引力，上演了一出笑泪交织的美好热恋。与此同时，其他成员也在这个过程中得到各自的成长。','//imgwx3.2345.com/dypcimg/img/d/65/sup197976_223x310.jpg',NULL,NULL,'1',4,1),
  (13,'全境警戒','片名“布希维克”本是美国纽约一个区的区名，故事也发生在这个地区。《布希维克》，男女主人公来到布希维克，本来轻松愉悦的两人却突然遭遇恐怖袭击，枪战、爆炸遍布城市，紧张窒息的情绪扑面而来。','//imgwx4.2345.com/dypcimg/img/a/66/sup198827_223x310.jpg',NULL,NULL,'91',4,1),
  (14,'烟花','暑假，某个海边小镇。在烟花晚会之前，热热闹闹的聊着“升空的烟花从侧面看是圆的？还是平的？”这样的话题的同班同学。与此同时，典道暗恋着的奈砂，因为奈砂的母亲的再婚对象已确定所以要转学了。“我们私奔、吧”奈砂向典道发出了一起逃出小镇的邀请，却在那之前被母亲带走了。而典道只是眼睁睁地看着这些。“如果，那时候我…”没有救下奈砂的典道，因为心中的苛责而丢出了奈砂在海中捡到的不可思议的珠子。就这样，不知何时起，时间被倒回到了奈砂被带走之前的时候…。一次又一次被重复的一天，奈砂与典道的命运会？','//imgwx4.2345.com/dypcimg/img/3/63/sup189366_223x310.jpg',NULL,NULL,'94',4,1),
  (15,'新白蛇传之青蛇','秦子矜，世家公子。有权有势，长相俊朗，因为天资聪颖，备受家族青睐，长辈喜欢，朋友拥护，看似一切完美，白悠悠，侠肝义胆，才貌双全，善良可爱，战乱，二人遭到了坎坷情劫..子矜竟为此成为女儿身,化身青蛇...','//imgwx4.2345.com/dypcimg/img/f/66/sup198882_223x310.jpg',NULL,NULL,'',4,1),
  (16,'小萝莉的猴神大叔','电影讲述了一个拥有虔诚宗教信仰的单纯印度男人帮助巴基斯坦哑女与父母重聚的故事。印度教教徒帕万在一次机缘巧合下结识了与母亲走失并有语言障碍的穆斯林小女孩沙希达。在得知沙希达是巴基斯坦人后，帕万决定帮助她回家，可在回巴基斯坦途中却四处碰壁，遭遇领事馆冲突、被旅游局欺骗之后，帕万立志将不惜一切代价尽其所能带沙希达回到自己的家乡与家人团聚。','//imgwx2.2345.com/dypcimg/img/c/60/sup180958_223x310.jpg',NULL,NULL,'140',4,1),
  (17,'战狼2','故事发生在非洲附近的大海上，主人公冷锋（吴京 饰）遭遇人生滑铁卢，被“开除军籍”，本想漂泊一生的他，正当他打算这么做的时候，一场突如其来的意外打破了他的计划，突然被卷入了一场非洲国家叛乱，本可以安全撤离，却因无法忘记曾经为军人的使命，孤身犯险冲回沦陷区，带领身陷屠杀中的同胞和难民，展开生死逃亡。随着斗争的持续，体内的狼性逐渐复苏，最终孤身闯入战乱区域，为同胞而战斗。','//imgwx3.2345.com/dypcimg/img/9/53/sup161557_223x310.jpg',NULL,NULL,'123',4,1),
  (18,'速度与激情8','多米尼克（范・迪塞尔 Vin Diesel 饰）与莱蒂（米歇尔・罗德里格兹 Michelle Rodriguez 饰）共度蜜月，布莱恩与米娅退出了赛车界，这支曾环游世界的顶级飞车家族队伍的生活正渐趋平淡。然而，一位神秘女子Cipher（查理兹・塞隆 Charlize T heron 饰）的出现，令整个队伍卷入信任与背叛的危机，面临前所未有的考验。','//imgwx2.2345.com/dypcimg/img/2/48/sup146828_223x310.jpg',NULL,NULL,'136',4,1),
  (19,'黑豹','在漫威影业的影片《黑豹》中，特查拉在其父亲――前瓦坎达国王去世之后，回到了这个科技先进但与世隔绝的非洲国家，继任成为新一任“黑豹”及国王。当旧敌重现时，作为“黑豹”及国王的特查拉身陷两难境地，眼看着瓦坎达及全世界陷于危难之中。面对背叛与危险，这位年轻的国王必须联合同盟，释放“黑豹”全部力量，奋力捍卫他的人民和国土。','//imgwx2.2345.com/dypcimg/img/d/65/sup197529_223x310.jpg',NULL,NULL,'129',4,1),
  (20,'僵尸世界大战','本片将于2013年6月在美国上映。由科幻小说《僵尸世界大战》改编。《《僵尸世界大战》通过联合国战后调查人员对一场毁灭世界各国的僵尸战争幸存者的调查展现了一次虚构的世界大战。 故事背景设定在人类赢得僵尸大战的十年后，当年那场对抗僵尸的战争波及整个世界，差点毁掉了人类。布拉德・皮特在片中饰演一位联合国工作人员，负责对僵尸大战后的幸存者进行调查。','//imgwx3.2345.com/dypcimg/img/6/24/sup72719_223x310.jpg',NULL,NULL,'123',4,1),
  (21,'二次初恋','叶兰与路建国结婚多年，常常因生活中鸡毛蒜皮的琐事拌嘴，在第20个结婚纪念日，两人终于再起争执闹到离婚，路建国一气之下离家出走并失去联系。此时路建国的侄子路大民却突然出现，打乱了叶兰原本的生活：不仅和路建国儿子路小飞成为哥们，并帮他追求心仪的姑娘yoyo，还把路建国的情敌萧峰(黄征饰)狠狠整蛊了一番。更令叶兰感到奇怪的是，路大民不止长得跟年轻时的路建国一模一样，就连神态动作举手投足也毫无差别，令叶兰产生心动的错觉。而一直没有音讯的路建国也仿佛人间蒸发了一般，直到叶兰看到路大民跳出了只有路建国才会的路式旋转舞…','//imgwx1.2345.com/dypcimg/img/0/64/sup193059_223x310.jpg',NULL,NULL,'100',4,1),
  (22,'遇见你真好','当遇见爱情的时候才会发现，它就像万有引力，苹果总归会落地。在被称为“高四”的紫荆复读学校是一所爱情集中营，梦想成为作家的张文生和珊妮在互损中萌生情愫，好友陈奇误认为文生横刀夺爱，与“坏学生”青龙在小酒馆相遇并商议各自的复仇计划。不料陈奇带去的巨型烟花被青龙向酒馆发泄中引发火灾，带出人命。于是，这所学校里的一个又一个神秘搞笑的爱情故事浮出水面，一群热血而欢乐的男女演绎了另类的青春和爱情……','//imgwx4.2345.com/dypcimg/img/7/65/sup196637_223x310.jpg',NULL,NULL,'91',4,1),
  (23,'奇门遁甲','奇幻3D巨制《奇门遁甲》由徐克监制、编剧，袁和平执导，将于2017年12月15日贺岁档震撼公映。该片讲述的是一个妖孽横行，天象大乱的时代，外星妖物侵袭而来。此时江湖上精通“奇门遁甲”之术的神秘组织“雾隐门”挺身而出，其首领老大（伍佰 饰）、弟子诸葛青云（大鹏 饰）、铁蜻蜓（倪妮 饰）和雾隐七子等众人各怀所长。另一方面，诸葛青云途中寻找新一代掌门小圆（周冬雨 饰）之时，新晋捕快刀宜长（李治廷 饰）为救铁蜻蜓亦被牵连在内。一时间，风云涌起，“天外来客”与“雾隐门”之间的正邪之战即将引发，一场新的奇幻危机正在来临','//imgwx3.2345.com/dypcimg/img/f/65/sup196006_223x310.jpg',NULL,NULL,'112',4,1),
  (24,'寻梦环游记','小男孩米格（安东尼・冈萨雷斯 Anthony Gonzalez 配音）一心梦想成为音乐家，更希望自己能和偶像歌神德拉库斯（本杰明・布拉特 Benjamin Bratt 配音）一样，创造出打动人心的音乐，但他的家族却世代禁止族人接触音乐。米格痴迷音乐，无比渴望证明自己的音乐才能，却因为一系列怪事，来到了五彩斑斓又光怪陆离的神秘世界。在那里，米格遇见了魅力十足的落魄乐手埃克托（盖尔・加西亚・贝纳尔 Gael García Bernal 配音），他们一起踏上了探寻米格家族不为人知往事的奇妙之旅，并开启了一段震撼心灵、感动非凡、永生难忘的旅程。','//imgwx2.2345.com/dypcimg/img/3/63/sup191426_223x310.jpg',NULL,NULL,'100',4,1),
  (25,'杀破狼2','香港卧底探员志杰（吴京 饰）不惜变成瘾君子，潜伏犯罪集团侦查幕后主脑洪先生（古天乐 饰）；直属上司兼叔父华哥（任达华 饰）决定终止行动，志杰却身份败露且人间蒸发。华哥违抗命令私自搜寻，辗转得悉志杰已被关进泰国某监狱，遂只身前赴营救。 \r\n泰国警察阿猜（托尼・贾 饰）为筹钱医治患血癌的女儿转任狱警，负责看管志杰。志杰与阿猜语言不通、身份对立，却竟是唯一可拯救阿猜女儿的骨髓捐赠者，阿猜必须保住志杰的性命；但狱长（张晋 饰）为确保幕后交易运作畅正常，必须铲除志杰。 \r\n身体虚弱的洪先生带同胞弟（恭硕良 饰）突然出现，准备进行心脏移植手术自救；原来整个监狱是洪先生操控的活人体器官供应市场……','//imgwx1.2345.com/dypcimg/img/4/48/sup145988_223x310.jpg',NULL,NULL,'114',4,1),
  (26,'我说的都是真的','职业策划人夏至信口开河编了一个关于绑架的故事，却误打误撞说中事实，于是招来犯罪团伙追杀，连他暗恋的餐吧老板娘印小雪都被卷入其中，从而引发了一系列扑朔迷离而又妙趣横生的故事。','//imgwx4.2345.com/dypcimg/img/6/66/sup198606_223x310.jpg',NULL,NULL,'99',4,1),
  (27,'复仇者联盟','一股突如其来的强大邪恶势力对地球造成致命威胁，没有任何一个超级英雄能够单独抵挡。神盾局也措手不及，其指挥官“独眼侠”尼克・福瑞意识到他必须创建一个“史上最强”的组织，云集各方超级英雄共同战斗，才能拯救世界于水深火热，抵御黑暗势力的侵袭。于是“复仇者联盟”应运而生。','//imgwx1.2345.com/dypcimg/img/c/13/sup39641_223x310.jpg',NULL,NULL,'142',4,1),
  (28,'战犬瑞克斯','改编自真人真事，以伊拉克战争为背景，讲诉海军陆战队下士梅根・利维和她的军犬瑞克斯一起拯救了多人生命的故事。在经历生死与相互救赎后，一人一犬建立深厚情感的故事。 它在真实的背景下以一种特殊的方式升华了人与狗的情感。狗不再是宠物，而是以战友身份与人相处；它们不再生活在人类的庇护下，而是反过来，用自己的身躯保护人类。 该片以“军犬”这个新奇的角度还原伊拉克战争，首次全面展现军犬在战区排爆、救人的惊心动魄的画面，真实刻画了女兵梅根・利维与军犬瑞克斯在战场上紧张、勇猛的状态。','//imgwx1.2345.com/dypcimg/img/c/66/sup198912_223x310.jpg',NULL,NULL,'115',4,1),
  (29,'侏罗纪公园','哈蒙德（理查德阿滕伯勒 Richard Attenborough  饰）立志要建立一个非同寻常的公园：恐龙将是这个公园的主角。他把众多科学家收归旗下，利用琥珀里面困住的远古蚊子体内的血液，提取出恐龙的基因信息，利用这些信息培育繁殖恐龙。结果如愿以偿，他把怒布拉岛建立成了一个恐龙公园，坚信可以从中赚取大钱。然而，科学家们则忧心忡忡。\r\n不幸的事情果然发生了。虽然公园有电脑系统管理，但却因为被员工破坏而造成了无法挽救的失控：所有的恐龙逃出了控制区，人们纷纷逃窜却逃不过恐龙的魔爪。恐龙自相残杀，人们亦死难无数，最后幸存者寥寥，只得四人逃出生天。怒布拉岛上空弥漫着恐怖的气息。','//imgwx4.2345.com/dypcimg/img/6/17/sup52954_223x310.jpg',NULL,NULL,'126',4,1),
  (30,'小马宝莉大电影','一股新黑暗势力威胁到小马谷，六位小伙伴紫悦、苹果嘉儿、云宝、碧琪、柔柔和珍奇离开小马国踏上难忘之旅，在旅途中他们结交新朋友，遇到刺激的挑战，必须通过友谊的魔力拯救自己的家园。','//imgwx5.2345.com/dypcimg/img/1/65/sup196836_223x310.jpg',NULL,NULL,'20',4,1),
  (31,'巴霍巴利王2：终结','同蛮族的大战以摩喜施末底的胜利而告终，根据战场上的表现，希瓦伽米太后（拉姆亚・克里希南饰）选择了养子阿玛兰德拉・巴霍巴利（帕拉巴斯饰）作为王国的王储。在加冕典礼之前太后交给巴霍巴利的最后一项任务是游历王国，体察臣民的疾苦，理解''臣民的审判''。同时，太后还竭尽全力满足自己的亲子巴拉拉德夫德斯（纳拉・达古巴提饰）的各种需求，希望能够克制他的贪婪，平衡兄弟的关系。 \r\n　　巴霍巴利经过一个叫昆达拉的诸侯小国时，偶遇美丽绝伦、剑术高超的提婆犀那公主（安努舒卡・谢蒂饰），立刻惊为天人，无以自拔。同行的卡塔帕将军（萨蒂亚拉吉饰）灵机一动，让巴霍巴利装成傻子去接近公主，在妙趣横生的交往中，感情在慢慢滋生。这','//imgwx4.2345.com/dypcimg/img/e/66/sup198569_223x310.jpg',NULL,NULL,'142',4,1),
  (32,'复仇者联盟2：奥创纪元','托尼・史塔克想要启动一个搁置的维护世界和平的项目，但搞砸了。地球命悬一刻，复联成员除了需要对付老“朋友”奥创，还得分神应对两个神秘而强大的新“对手”：猩红女巫与快银。','//imgwx4.2345.com/dypcimg/img/3/41/sup125818_223x310.jpg',NULL,NULL,'141',4,1),
  (33,'南极之恋','南极，一场坠机，婚庆公司老板吴富春和高空物理学家荆如意相遇，两个毫无共同语言的男女在南极腹地无人区冒险生存75天。在酷寒、没有物质供应、随处都是绝境的环境中，活下去已经变成每一天的最大愿望。在寻找救援的过程中，这对性格水火不容但命运却又相濡以沫的人，敬畏了自然，领悟了爱情。','//imgwx1.2345.com/dypcimg/img/a/65/sup196560_223x310.jpg',NULL,NULL,'117',4,1),
  (34,'前任3：再见前任','一对好基友孟云和余飞跟女友都因为一点小事宣告分手，并且“拒绝挽回，死不认错”。两人在夜店、派对与交友软件上放飞人生第二春，大肆庆祝“黄金单身期”，从而引发了一系列好笑的故事。孟云与女友同甘共苦却难逃“五年之痒”，余飞与女友则棋逢敌手相爱相杀无绝期。然而现实的“打脸”却来得猝不及防：一对推拉纠结零往来，一对纠缠互怼全交代。两对恋人都将面对最终的选择：是再次相见？还是再也不见？','//imgwx2.2345.com/dypcimg/img/c/64/sup194620_223x310.jpg',NULL,NULL,'95',4,1),
  (35,'赤雷传','商朝初期，华夏大地上列国纷争不断，豪强并起，硝烟弥漫。赤雷就在这样的环境下出生了。因生来就是一头红发，脸上还闪着蓝光，赤雷的母亲即使不舍，最终仍是狠心地将襁褓里的赤雷遗弃在了森林的深处，独自离去。被抛弃的赤雷在森林险恶的环境之下幸运的存活下来，健康地长大了。随着他长大，背上也慢慢的长出了一对翅膀......','//imgwx2.2345.com/dypcimg/img/a/66/sup198842_223x310.jpg',NULL,NULL,'80',4,1),
  (36,'起跑线','一对印度的中产阶级夫妇:服装店老板拉吉（伊尔凡・可汗 Irrfan Khan 饰）与太太米塔（萨巴・卡玛尔 Saba Qamar 饰）为了让女儿皮娅（蒂希塔・塞加尔 Dishita Sehgal 饰）接受更好的教育想尽了各种办法。而当他们费劲心思终于要将女儿送进名校时,事情却又发生了意想不到的变化。','//imgwx1.2345.com/dypcimg/img/d/66/sup198265_223x310.jpg',NULL,NULL,'92',4,1),
  (37,'杨戬传','商朝初年某天，杨戬狼狈从天而降，记忆受损。附近村子的村女叶子发现后，将其救回。担心杨戬孤独无依的叶子，顶着村民的压力坚持将他留了下来。两人在平淡的生活中渐渐相爱，直到一伙强盗前来洗劫，打破了这平静。为救爱人，杨戬不得不成为了一名斗祭士，在战斗中逐渐激发了自己第三只眼睛的力量。一直潜伏在杨戬身边的搭档青凰也在寻找机会恢复他的记忆，原来这一切都是移魂异人的阴谋所致……','//imgwx2.2345.com/dypcimg/img/7/66/sup198588_223x310.jpg',NULL,NULL,'20',4,1),
  (38,'侏罗纪世界','21世纪，曾经诞生过第一只恐龙的努布拉岛，在经历了一系列的惨剧之后迎来新的时代，如今这里经马斯拉尼之手建成了规模宏大的主题公园“侏罗纪世界”，数量繁多的远古生物每天都吸引数以万计的游客前来一饱眼福。为了吸引更多的游客，公司授意创意实验室采用不同动物的基因创造了集力量、速度和智慧于一体的暴虐霸王龙。作为负责人的克莱尔（布莱丝・达拉斯・霍华德 Bryce Dallas Howard 饰）原打算请专门负责迅猛龙的欧文・格雷迪（克里斯・普拉特 Chris Pratt 饰）评估暴虐霸王龙的防护设施，谁知这头可怕的怪物竟然骗过了精密仪器的监视和人类的眼睛，以迅雷不及掩耳的速度逃到了外面，随后对所见的一切生','//imgwx2.2345.com/dypcimg/img/1/45/sup137471_223x310.jpg',NULL,NULL,'124',4,1),
  (39,'黄金罗盘','故事发生在一个和我们的地球很相似的世界里，那里人们的灵魂变成灵神跟随自己。莉拉自幼跟随身为约旦大学校长的叔叔一起生活，她热情、勇敢且富有正义心。一天科尔特夫人要到北方去，希望莉拉能伴随身边。好奇的莉拉开心地答应。临行前叔叔交给她一个黄金罗盘，告诉她世界上只有一个人能读懂。莉拉从黄金罗盘中不仅能得到所问问题的答案，还了解到了自己的身世之谜。这一路上，科尔特夫人行为诡异，莉拉还发现在特尔特夫人的一份名单上赫然写着自己失踪朋友罗杰的名字！罗杰和其他孩子一样，被诱拐到极北之地成为科学试验品。莉拉找到时机逃跑出来，自己动身前去拯救罗杰……','//imgwx4.2345.com/dypcimg/img/5/7/sup22107_223x310.jpg',NULL,NULL,'113',4,1),
  (40,'疯魔鲁智深','北宋年间，花和尚鲁智深三拳打死了镇关西，逃至五台山文殊院落发为僧。却因控制不住杀念，被长老安排至东京汴梁大相国寺潜心修道。在这途中，他屡见世间不平之事却难以出手，因内心的对过往杀孽的忏悔而深受煎熬。终于，在目睹了官贼相护残杀无辜的可怕恶行之后，怀着报仇之愤的他拾起禅杖陷入疯魔.......','//imgwx3.2345.com/dypcimg/img/8/66/sup198241_223x310.jpg',NULL,NULL,'86',4,1),
  (41,'哆啦A梦：大雄的南极冰冰凉大冒险','夏天来了，经不住大雄的百般纠缠，哆啦A梦第一次同意带着小伙伴们来到南极，并建造了专属的南极乐园。 没想到这场消夏之旅，被偶然发现的金环打断，快乐的旅程就此中止，他们来到十万年前的南极。这一次，哆啦A梦面临生死考验，地球也面临全面冻结的危险。没有了哆啦A梦，小伙伴们只能团结一心，才能拯救地球！','//imgwx4.2345.com/dypcimg/img/c/64/sup192621_223x310.jpg',NULL,NULL,'101',4,1),
  (42,'极速复仇','经历了长达十年常人难以想象的牢狱生活，绰号“司机”的男子重返自由。但是他并不想就此展开平静的日子，而是迅速向当年的仇人展开血腥复仇。十年前，司机和哥哥加里等人打劫银行，并凭借其出色的车技成功摆脱警察的追捕。然而他们却遭到另一伙人的暗算，现金被一抢而空，哥哥和伙伴们遭到杀害，司机也鬼门关前走上一遭。凭借手中的资料，司机逐一展开报复。与此同时，手法干练的杀手和即将退休的警察也紧随司机的脚步。三个人展开了一场极速的生死追逐战','//imgwx2.2345.com/dypcimg/img/c/14/sup42045_223x310.jpg',NULL,NULL,'97',4,1),
  (43,'恐龙（国语版）','小禽龙阿拉达生活在白垩纪晚期，他自小就生活在一个桃花园般的小岛上。由一群狐猴抚养长大大的阿拉达从来就没有见过自己的同类，与他的狐猴朋友一直无忧无虑的生活在一起。一场不期而至的流星雨将阿拉达所在的小岛击毁了，阿拉达和他的狐猴朋友一起逃难来到了大陆。这时，因为大气变热，水源供给越来越紧张，阿拉达他们遇上了一群正在迁徙寻找新的栖身之地的恐龙并加入了他们。于是，阿拉达和同伴们一起开始了这次异常艰难的迁徙之旅。','//imgwx1.2345.com/dypcimg/img/c/13/sup40417_223x310.jpg',NULL,NULL,'82',4,1),
  (44,'罪途1之死亡列车','本部影片是《罪途》系列三部曲的第一部，讲述了列车因暴雨引发泥石流而被停在半路。一节车厢中的八名乘客，全部陷入了不明原因的昏迷当中。调查结果则更让人心惊，其中一位乘客已经成为了冰冷的尸体……','//imgwx4.2345.com/dypcimg/img/0/66/sup198744_223x310.jpg',NULL,NULL,'20',4,1),
  (45,'失落的大陆','古生物学学家瑞克马绍尔提出了时光旅行的设想，并征得1500万美元的经费专门开发穿越时空的超光速粒子仪。然而公众对此并不买帐，反而对其大肆嘲笑和抨击。3年来，瑞克无奈窝在一所小学里教书，自暴自弃。某天，来自剑桥的美丽女子霍莉找到瑞克，她展示了一块不可思议的化石，以此鼓励瑞克继续他的研究。重获信心的瑞克最终制造出超光速粒子仪，他和霍莉来到化石的发现地，准备进行时光旅行的测试。在有些神经质的威尔的带领下，他们划着皮筏漂流。不料一场强烈地震突然袭来，三人被狂卷的波涛带入一个未知的世界，一场充满惊险和刺激的探险就此展开…','//imgwx5.2345.com/dypcimg/img/1/11/sup34909_223x310.jpg',NULL,NULL,'101',4,1),
  (46,'忌日快乐','生日当晚惨遭杀害的泰莉，一觉醒来仍然生勾勾，她以为只是生日前夕的恶梦，怎知醒来后的经历跟梦境完全一样，生日当晚再惨遭杀害，然后又一觉醒来…究竟是上天的大礼，还是死神的玩笑，她竟然无限次被杀，再无限次重返生日早上！泰莉醒完又死，死完又醒，她唯一可以做的，就是找出杀自己的神秘凶手，否则会被困在生日和死忌的无限轮回，直到永远','//imgwx4.2345.com/dypcimg/img/f/65/sup196625_223x310.jpg',NULL,NULL,'92',4,1),
  (47,'功夫瑜伽','考古学教授Jack（成龙 饰）多年没有踏出学校，校长和学生都对他添油加醋的历史半信半疑。一次授课后，一个印度女孩找到Jack，用一张千年地图吸引了Jack的目光。于是Jack和挚友的儿子李琼斯（李治廷 饰）、助教小光（张艺兴 饰）组成“寻宝小分队”，为“神秘宝石”展开冒险，在迪拜和土豪飙车，在冰岛潜入神秘冰穴，在印度大战土狼，和印度贵族斗舞，动作喜剧再升级，打笑全世界。','//imgwx4.2345.com/dypcimg/img/a/60/sup180771_223x310.jpg',NULL,NULL,'107',4,1),
  (48,'地心历险记','虽然特雷弗（布兰登・费舍 Brendan Fraser 饰）是大学的地址教授，但是却经常做出挑战传统的事情。他认为，凡尔纳的小说并不是科幻，而是真是存在的。就是因为这样的对立理论，使其被传为学院里的笑话，使得他的学术名声毁于一旦。不过特雷弗却毫不在意这些，他坚信自己的理论是正确的。为了验证自己的理论，特雷弗带着侄子肖恩（乔什・哈切森 Josh Hutcherson 饰）和当地的向导汉娜（安妮塔・布瑞姆 Anita Briem 饰）开始了在冰岛的旅行。在旅行时，三人意外发现一个奇特的洞穴，并在探询时踏破薄冰跌入了无底深渊。不过，当他们醒来时，却发现了一个神秘的地心世界，里面有着各种稀奇古怪的生物','//imgwx5.2345.com/dypcimg/img/b/9/sup28871_223x310.jpg',NULL,NULL,'92',4,1),
  (49,'大闹天竺','盛唐集团总裁唐宗突然离世并留下遗训，让他的儿子唐森在穷小子武空的陪同下前往印度寻找遗嘱。在印度巧遇自恋臭美却又忠诚的朱天鹏，以及美丽性感却深藏秘密的美女吴静，四人兜兜转转竟结为同盟，而最令四人不解的是为何这次取遗嘱之旅凶险重重，危机四伏,并且遗嘱之所以放在印度，更是隐藏着秘密。','//imgwx4.2345.com/dypcimg/img/5/55/sup165988_223x310.jpg',NULL,NULL,'99',4,1),
  (50,'史前一万年','史前一万年的世界，神秘而幽远。在终年为白雪覆盖的大白山上，生活着以打猎为生的亚高族。他们擅长捕猎猛犸象，然而随着猛犸象越来越少，亚高族也逐渐走向末路。具有预知能力的老妈妈临死前做出预言：亚高族将面临四脚恶魔的入侵，届时族人将迎来最后一次狩猎，在此之后一位伟大的战士就会出现，并迎娶躲避战乱来到部落的蓝眼睛小女孩伊芙乐为妻，最终带领亚高族远离饥饿。 族人们对此深信不疑，耐心等待决定命运的时刻的到来。男孩达雷和卡恩对伊芙乐都心存爱慕。某日，消失十多年的猛犸象群重新回到大白山，达雷阴差阳错杀死一头猛犸象，赢得了女孩的心。不久，野蛮的外族入侵，虏走了美丽的伊芙乐。为了找回爱人，达雷和伙伴们踏上了充满艰险','//imgwx4.2345.com/dypcimg/img/5/19/sup59250_223x310.jpg',NULL,NULL,'108',4,1),
  (51,'阿凡达','故事发生在2154年，故事从地球开始，杰克・萨利是一个双腿瘫痪的前海军陆战队员，他觉得没有任何东西值得他去战斗，因此他对被派遣去潘多拉星球的采矿公司工作欣然接受。 这个星球上有一种别的地方都没有的矿物元素“Unobtanium”，能够吸引人类不远万里来到这里拓荒的原因就是“Unobtanium”将彻底改变人类的能源产业。但是问题是，资源丰富的潘多拉星球并不适合人类生活，这里的空气对人类致命，本土的动植物都是凶猛的掠食者，极度危险。这里的环境也造就了与人类不同的种族：10英尺高（约3米）的蓝色类人生物“。','//imgwx4.2345.com/dypcimg/img/a/7/sup22347_223x310.jpg',NULL,NULL,'161',4,1),
  (52,'极速酷客','机车玩家卡利福特回到老家，跟女友夏恩重逢，顺便收拾一些没解决的事情。 但是两个月前离开的时候，他还是飞车党羽的一员，听命于残忍无情的毒贩老大亨利。如今亨利无所不用其极，就是要卡利重新为他效命；当卡利不从，亨利便陷害他成为杀人凶手，而死者恰好正是令人闻风丧胆的飞车党、死神帮、老大泰瑞的弟弟。','//imgwx1.2345.com/dypcimg/img/1/0/sup1567_223x310.jpg',NULL,NULL,'83',4,1),
  (53,'祖宗十九代','一个在现实中失意的青年作家小贝,在现实碰壁而低落的夜晚,经历穿越20世纪初拯救陷入帮派麻烦的祖先、清朝和参加科举考试的先祖共事、明朝与祖先共同御敌、晋朝和祖先一起救世济人、上古与女娲和夸父探讨生命的本源和意义的奇幻旅程。一路见证爱情、收获自信和担当,经历人性的转变而自我成长,奇幻之旅实则他的新书发布,这本书献给他未见过的母亲和祖辈们。','//imgwx2.2345.com/dypcimg/img/c/65/sup195888_223x310.jpg',NULL,NULL,'20',4,1),
  (54,'霸王别姬','段小楼（张丰毅）与程蝶衣（张国荣）是一对打小一起长大的师兄弟，两人一个演生，一个饰旦，一向配合天衣无缝，尤其一出《霸王别姬》，更是誉满京城，为此，两人约定合演一辈子《霸王别姬》。但两人对戏剧与人生关系的理解有本质不同，段小楼深知戏非人生，程蝶衣则是人戏不分。\r\n段小楼在认为该成家立业之时迎娶了名妓菊仙（巩俐），致使程蝶衣认定菊仙是可耻的第三者，使段小楼做了叛徒，自此，三人围绕一出《霸王别姬》生出的爱恨情仇战开始随着时代风云的变迁不断升级，终酿成悲剧。','//imgwx4.2345.com/dypcimg/img/7/1/sup5634_223x310.jpg',NULL,NULL,'164',4,1),
  (55,'新白蛇传之法海','围绕龙吉公主、小青、法海、九尾狐、黑山老妖而展开，虽是五个独立的故事，却又和白蛇传说有着密不可分的联系，挖掘了更多鲜为人知的故事，让白蛇传中的每一位主人公更加饱满和鲜活的呈现在观众面前。','//imgwx1.2345.com/dypcimg/img/5/66/sup198481_223x310.jpg',NULL,NULL,'',4,1),
  (56,'僵尸先生4：僵尸叔叔','偏僻山间，一僧一道比邻而居。和尚（午马 饰）随和开朗，更有一女徒菁菁（李丽珍 饰）玲珑可爱；道士（陈友 饰）古板严厉，而徒弟嘉乐（钱嘉乐 饰）却鬼马顽皮。性格不同的两个师傅口角不断，大斗其法，两个冤家徒弟也搞笑连连，笑料频出。某天，一支奇异的丧葬队伍路过此地，某边疆皇族中尸毒死去，幻化成僵尸，千鹤道长及王子护送棺木上京。途中遭遇暴雨，棺木被雷劈开，僵尸破棺而出，大开杀戒。乌侍郎（元华 饰）携王子侥幸逃命，但王子已中尸毒。和尚与道士命徒儿照看王子，两人则冲入树林寻找千鹤道长……','//imgwx1.2345.com/dypcimg/img/9/7/sup21781_223x310.jpg',NULL,NULL,'89',4,1),
  (57,'哥斯拉（2014）','拥有幸福美满生活的大兵福德・布罗迪（亚伦・泰勒-约翰逊 Aaron Taylor-Johnson 饰），内心隐藏着一个挥之不去的阴影。15年前，他和父亲乔伊（布莱恩・科兰斯顿 Bryan Cranston 饰）、母亲桑德拉（朱丽叶・比诺什 Juliette Binoch e 饰）生活在日本。父母供职于一座大型核电站。就在福德的爸爸生日当天，一场突如其来的灾难摧毁了核电站，他的母亲在灾难中丧生，语焉不详的政府隐瞒真相，以防止核辐射的名义封锁了该地区。但是乔伊却从种种异状觉察到反常之处，十五年来他一直试图揭开当天的灾难之谜，终于发现这一切都是由一只如山一般巨大的怪兽所引起。怪兽哥斯拉从沉睡中苏醒，','//imgwx1.2345.com/dypcimg/img/3/36/sup109062_223x310.jpg',NULL,NULL,'120',4,1),
  (58,'诸神之怒','结束了与北海巨妖的战争，英雄珀修斯（萨姆・沃辛顿 Sam Worthington 饰）与儿子赫利俄斯（约翰・贝尔 John Bell 饰）在一座小村庄过着平静的生活。但是奥林匹亚的战火从未停歇，以宙斯（连姆尼森 Liam Neeson 饰）为首的众神与泰坦巨人进行着旷日持久的惨烈战争，甚至连被宙斯囚禁良久的三神（天神宙斯、海神波塞冬、冥王哈迪斯）之父――克洛诺斯也即将复活。战争的天平渐渐向泰坦巨人一方倾斜，在冥王哈迪斯（拉尔夫・费因斯 Ralph Fiennes 饰）的策划下，宙斯误入冥界，神力尽失，遭到囚禁。为了驱逐黑暗，将光明带回大地，珀修斯重披铠甲，与一众英雄前往冥界营救宙斯，诸神的战火','//imgwx4.2345.com/dypcimg/img/f/15/sup46886_223x310.jpg',NULL,NULL,'99',4,1),
  (59,'守护者：世纪战元','一直从事兵器研究的邪恶的克拉托夫教授欲用生物兵器达成自己的野心，在俄罗斯境内的各大城市制造了一系列大规模爆炸恐怖袭击。为了消灭邪恶势力，拯救面临生物兵器毁灭的国家，俄罗斯国防部派出了冷战时期创建的名为“爱国者”的超级英雄团队，各个英雄异于常人，身怀特技，与号称统 治世界的邪恶教授展开了一场殊死较量。','//imgwx3.2345.com/dypcimg/img/0/63/sup190377_223x310.jpg',NULL,NULL,'89',4,1),
  (60,'魔国志I之黄巾之乱','东汉末年，张角手持《风遁天书》，一边治病救人，一边蛊惑民心，妄想改朝换代，黄巾起义各地揭竿而起。起义只是个开始，随之而来的是战火四起，民不聊生。曹操、刘备等忠义之士，以匡扶汉室为己任，欲在乱世建功立业。以两人为首，郭伽、关羽、香香等组成的五人组，结伴而行，一路披荆斩棘，历尽艰险。通过香香的关系，得到了孙坚军队的加盟，最终与张角殊死一战，众人皆陷入张角的幻境中，危难之际，香香选择牺牲自己，破除五感，解救了众人，张角被杀，成功平定了黄巾之乱。','//imgwx5.2345.com/dypcimg/img/c/65/sup197864_223x310.jpg',NULL,NULL,'',4,1),
  (61,'杀手：代号47','代号47是一个利用基因技术制作的复制人，受雇于名为“The Agency”的组织进行刺杀活动，47是他颈背条码的最后两个数字，也是他的名字。因为卷入了政治阴谋，47在东欧遭到了国际刑警和俄国军队的双重追杀。在追查真凶的过程中，他遇上了一个致命的女人。杀手的冷酷无情渐渐被女人瓦解，但是生命也正在受到威胁。','//imgwx1.2345.com/dypcimg/img/0/50/sup150883_223x310.jpg',NULL,NULL,'87',4,1),
  (62,'巨人捕手杰克','曾几何时，云端的巨人依靠通天豆荚来至大地，他们横行肆虐，吞噬人类，祸害无边。多亏勇敢机敏的埃里克国王将巨人赶回老家，巨人的传说从此越传越神，流传甚广。自幼深深为巨人故事所吸引的穷人家青年杰克（尼古拉斯・霍尔特 Nicholas Hoult 饰）进城买马，偶然遭遇一名被皇家卫队追捕的僧侣，他更在慌乱中用马换了对方手中的豆荚。夜幕降临，暴雨突至，不愿将毕生幸福付与他人的公主伊莎贝尔（艾莉诺・汤姆林森 Eleanor Tomlinson 饰）趁乱出走，辗转来到杰克家中避雨。正当他们聊天之际，被雨水浸透的豆荚突然发芽，豆茎疯长，直达天际，将困在屋中的公主带入云端。\r\n为了找回公主，杰克自愿加入国王组建','//imgwx4.2345.com/dypcimg/img/8/29/sup89956_223x310.jpg',NULL,NULL,'114',4,1),
  (63,'修罗狱之彼岸花','江湖上，人人都知道韩瞎子要找孤鸿报仇。事情的真相是这样的，几年前韩瞎子还不是瞎子的时候，他和孤鸿是天造地设的一对。男的天下无敌，女的风华绝代。天下间人人羡慕。但是当天下人竞相争夺的秘宝出现时，韩瞎子却因为一件天下间最神秘的秘宝而刹那间忽略了孤鸿，于是悲剧就上演了。孤鸿挖走了男人的眼睛，并带走了秘宝，江湖上从此开始了一场更大的血雨腥风……','//imgwx1.2345.com/dypcimg/img/a/66/sup198664_223x310.jpg',NULL,NULL,'',4,1),
  (64,'喵星人','千百年来，地球上一直住着一种外星生物，名叫喵星人。它们从遥远的喵星来到地球，化身为猫科动物，分散在世界每个角落。萌萌的外表，加上机智聪明的头脑，轻易就得到人类的宠爱。不愁吃喝的它们桀骜不驯，活像饱经沧桑的江湖大佬。犀犀利就是他们中的一员，作为一名正牌的喵星来客，它肩负使命长途跋涉来到地球，却意外遇到了吴守龙（古天乐饰演）一家，上演了一幕相运相生，喵趣横生的人猫奇遇记。','//imgwx2.2345.com/dypcimg/img/b/64/sup192093_223x310.jpg',NULL,NULL,'108',4,1),
  (65,'白骨夫人大战狼妖','夜灵真的觉得自己无比凄凉，不就是学了五百年法术还是半吊子？被强行扔到人间，却还没等反应过来就被地痞打了一顿。虽然听师傅话找到了转世灵花，却也被同类找上门，被绑架，被殴打，所有倒霉的事情都遇见一个遍。当他发现爱上了转世灵花，随即就必须接受转世灵花被仇人白玲珑杀了。原来这一切都要从五百年前说起，白玲珑因为丈夫狼王的背叛，杀了夜灵的母亲，还要屠杀老百姓的时候被狼王用嗜血咒封印。五百年后，白玲珑卷土而来....','//imgwx3.2345.com/dypcimg/img/f/66/sup198592_223x310.jpg',NULL,NULL,'80',4,1),
  (66,'大话西游之大圣娶亲','至尊宝（周星驰）被月光宝盒带回到五百年前，遇见紫霞仙子（朱茵），被对方打上烙印成为对方的人，并发觉自己已变成孙悟空。 \r\n　　紫霞与青霞（朱茵）本是如来佛祖座前日月神灯的灯芯（白天是紫霞，晚上是青霞），二人虽然同一肉身却仇恨颇深，因此紫霞立下誓言，谁能拔出她手中的紫青宝剑，谁就是她的意中人。紫青宝剑被至尊宝于不经意间拔出，紫霞决定以身相许，却遭一心记挂白晶晶（莫文蔚）的至尊宝拒绝。后牛魔王救下迷失在沙漠中的紫霞，并逼紫霞与他成婚，关键时刻，至尊宝现身。','//imgwx3.2345.com/dypcimg/img/8/0/sup665_223x310.jpg',NULL,NULL,'99',4,1),
  (67,'雷神3：诸神黄昏','雷神失去了他强大的锤子，并被囚禁在宇宙的另一端。而他发现自己必须与时间赛跑才能赶回仙宫阻止诸神黄昏！家乡的毁灭以及仙宫文明的终结掌握在一个全新的超强劲敌手里――她就是无情的死神海拉！然而他必须要在一场角斗赛中获胜才行，而他的对手将是昔日复联伙伴――无敌浩克！','//imgwx2.2345.com/dypcimg/img/5/49/sup149445_223x310.jpg',NULL,NULL,'125',4,1),
  (68,'捉妖大仙','燕十七偶然间来到装扮成酒家营业的妖楼，他拔剑降妖除魔。其中一名妖怪赤霞侥幸逃走，燕十七追去，却见寻妖罗盘异响，只见寻妖罗盘指针朝向北方，他登高远望，只见远处妖气冲天。童子村中，热血捕快王美丽同搭档花想容在执行任务的过程中，无意中救下被燕十七所伤的赤霞，赤霞对王美丽日久生情。此时燕赤霞来到童子村，正欲降妖除魔，却被村民当做杀人狂魔关入柴房。在柴房的菜缸里，他们发现了为了躲避虫妖羽玄姬追杀逃离皇宫的异域皇帝同他的贴身太监。异域皇帝告知王美丽，想杀死羽玄姬，必须要找到当年姜子牙遗落人间的上古罗盘，并用万妖之王的血来为罗盘开封。此时，童子村却突然发生了命案……','//imgwx5.2345.com/dypcimg/img/5/66/sup198253_223x310.jpg',NULL,NULL,'',4,1),
  (69,'京城81号2','《京城81号2》取材自东方四大鬼宅之首的朝内81号地宫真实故事。民国末期，正逢乱世，京城军阀哗变，少帅张骘生（张智霖 饰）被逼迎娶大军阀之女纪金翠（钟欣潼 饰），随即府内怪事丛生，人心惶惶。不久后京城涌现大批“尸婴案”，一切矛头指向少帅原配夫人钮梦鹤（梅婷 饰），随后钮梦鹤离奇失踪，一夜之间京城81号惨遭灭门，留下一座破败鬼宅。百年后，文物修复师宋腾在修复古宅过程中挖出大量婴儿尸骨与诡异符咒，一桩桩血淋淋的旧事似乎要将所有人拉进深渊，前世今生的恩怨情仇都一一浮现，而地宫冤魂鬼门索命的故事也就此拉开……','//imgwx1.2345.com/dypcimg/img/0/53/sup160522_223x310.jpg',NULL,NULL,'96',4,1),
  (70,'异形魔怪','在一个名为“完美”的宁静小镇上，米奇（凯文・贝肯 Kevin Bacon 饰）和死党巴斯特（弗莱德・沃德 Fred Ward 饰）又度过了属于他们的平静一天。对于在这个无趣的小镇里从事着一份又一份没有前途，仅能糊口的工作这一事实，两个血气方刚的年轻人感到十分无奈和困扰。终于，他们决定要走出完美镇，开始他们多姿多彩的新生活，而就在此时，通向镇外的唯一一条公路莫名塌陷了，某些不详的征兆开始显现。早在数星期之前，镇上发生了家畜神秘失踪的恐怖事件，同时，地质系学生蕾贝卡（ 芬妮・卡特 Finn Carter 饰）发现小镇的地壳运动十分异常。现在，众人终于将这些纷乱的线索拼凑到了一起，在大家的脚下，黑不','//imgwx2.2345.com/dypcimg/img/4/10/sup30908_223x310.jpg',NULL,NULL,'95',4,1),
  (71,'神魔觉醒','超能力者从原始社会就已存在，他们散布于人类社会之中。和普通人类一样，超能力者有好也有坏，像伏羲、女娲、黄帝这样利用超能力领导和造福人类的被奉为了神灵，而像饕餮、蚩尤、妲己这种为了一己私欲妄图奴役和破坏人类社会的则被贬斥为妖魔。几千年来，正邪两派的争斗一直持续着，其中一些超能力者组织了起来与对方抗衡，代表正派的被称为“神力卫”，而反派则自诩为“魔游侠”，双方因为势均力敌而保持着一种平衡。根据上古传说，每隔数百年，就会有一名拥有神级超能力的觉醒者现世，哪一方能得到觉醒者，胜利的天平就会像哪一方倾斜，于是寻找觉醒者也成为了双方世代传承的任务，直到今天。','//imgwx2.2345.com/dypcimg/img/f/66/sup198244_223x310.jpg',NULL,NULL,'70',4,1),
  (72,'航班蛇患','黑帮老大艾迪金策划了一起凶杀案，被偶然路过的年轻人西恩琼斯（Nathan Phillips 饰）目击。为了在法庭上成功指控这个作恶多端的黑社会头子，警方派出资深联邦密探尼维利费恩（Samuel L. Jackson 饰）负责保护西恩，并将其从夏威夷护送至洛杉矶法庭。为避免证人遇害，尼维利采取声东击西的策略，与西恩偷偷乘上一架普通的红眼航班。\r\n然而这项绝密的计划被艾迪安排在警方的内奸获知，为了逃脱法律制裁，艾迪派出携带大量毒蛇的杀手登上飞机，一场离奇恐怖的血腥灭口案件就此展开……','//imgwx1.2345.com/dypcimg/img/a/0/sup1610_223x310.jpg',NULL,NULL,'104',4,1),
  (73,'大话西游之月光宝盒','本片为《大话西游》的上半部。故事开始是观音菩萨要除掉孙悟空(周星驰饰)，因其伙同牛魔王欲杀害师父唐三藏(罗家英饰)，并偷走了月光宝盒。唐三藏慈悲为怀，愿自杀以换悟空重生。\r\n　　五百年后，孙悟空投胎为强盗头头至尊宝。不巧撞见预谋吃唐僧肉的妖怪姐妹--蜘蛛精春三十娘(蓝洁瑛饰)和白骨精白晶晶(莫文蔚饰)。原来，500年前孙悟空和白晶晶曾有一段恋情，因而白晶晶与至尊宝一见钟情。此时，菩提老祖将二人的妖怪身份告诉了至尊宝，并同强盗们一起与二妖展开周旋。白晶晶为了救至尊宝打伤了春三十娘，却中毒受伤。至尊宝为了白晶晶来找春三十娘，却遭晶晶误会。白晶晶绝望自杀。至尊宝用月光宝盒使时光倒流却倒流回5……','//imgwx4.2345.com/dypcimg/img/b/0/sup364_223x310.jpg',NULL,NULL,'83',4,1),
  (74,'三伏天','小镇青年露露和白龙是一对同居在一起的情侣，他们没有结婚，但却有一个还在哺乳期的孩子。露露晚上在夜场跳舞，希望借助自己的努力能让他们和他们的孩子过上更好的生活。一个炎热夏天的晚上，下班后的露露回到住处却发现自己被锁在了门外，白龙和孩子莫名失踪了。为了找回孩子，他找到了白龙的男友sunny带她去找孩子。做为交换，她答应找到孩子之后就放过他们，让他们“自由”。但是，在真的找到孩子之后，所有人内心的秘密都掩藏不住了。','//imgwx3.2345.com/dypcimg/img/2/65/sup197532_223x310.jpg',NULL,NULL,'95',4,1),
  (75,'星河战队：入侵','在浩瀚的宇宙深处，驻扎在某小行星的地球联邦军队凯西堡垒遭到宿敌虫子大军的攻击。强袭舰雅丽莎号的彪勇战将奉命救援，在这个沦陷的基地内，却发生令人费解的一幕。超能力战略官员卡尔・詹金斯博士将卡门所负责的战列舰约翰・A・沃顿号的指挥权夺走，不知下落。战争的最后，雅丽莎号的战士们救出幸存者，小星星也在爆炸中灰飞烟灭。为了查清消失的战列舰的下落，强尼・瑞克命令返航途中的雅丽莎号迅速掉转船头，追赶约翰・A・沃顿号。效用彪悍的勇士们合二为一，开始了新的漫漫征途……\r\n本片为《星河战队》系列发表15周年纪念作品。','//imgwx4.2345.com/dypcimg/img/e/17/sup51806_223x310.jpg',NULL,NULL,'85',4,1),
  (76,'金刚（2005）','导演率领拍摄组来到神秘的骷髅岛开始了一部电影的拍摄，女主角是漂亮娇小的安(娜奥米沃茨 Naomi Watts 饰)，她在纽约的演艺事业陷入了低谷，这是她时来运转的大好机会。他们乘坐轮船来到岛上，却不知在岛上即将要险象横生。\r\n正当导演要开始开机拍摄时，他们遭到了当地土著的袭击，几个随行人员遇难身亡。摄制组慌忙逃离，幸好起航离去，却发现不见了女主角安。\r\n原来安土著野人抓走，被当作祭品。安尖声惊叫，引来了传说中骷髅岛的猩猩――金刚的关注。金刚在岛上很有霸气，连恐龙都要忌它几分。然而，野兽爱上了美人，金刚搭救安脱离困境，不料却让自己陷入了一场悲剧。','//imgwx4.2345.com/dypcimg/img/6/17/sup52980_223x310.jpg',NULL,NULL,'200',4,1),
  (77,'巨额来电','派出所年轻民警丁小田抓捕电信诈骗罪犯，与警方电信诈骗专案组不谋而合，专案组已经派出女特警小兔，混入远在泰国阿海与阿芳的骗子团队搜集证据，丁小田与卧底女特警小兔密切配合，与阿海、阿芳诈骗集团展开扣人心弦的反诈智斗，逐步掌握了犯罪团伙的诈骗各个环节机密与证据，但同时阿海也与人立下30天骗下1亿人民币惊天赌约，多位商业巨贾及大量老百姓卷入其中……','//imgwx4.2345.com/dypcimg/img/8/65/sup195114_223x310.jpg',NULL,NULL,'122',4,1),
  (78,'钢铁侠3','在《钢铁侠3》中，托尼・斯塔克将遭遇到一个能力似乎没有界限的强敌的挑战。这个人毁坏了斯塔克的生活，而斯塔克[3]只有依靠着自己精良的高科技的装备才能去寻找究竟是谁才是幕后的元凶。在寻找的过程中，斯塔克非常依赖自己的钢铁服，每一次转折都在测试着他的勇气。当他最终找到强敌， 并且准备展开反戈一击的时候，斯塔克顿时发现了自己一直以来都面对着一个巨大的问题：到底是战衣造就了一个男人，还是这个男人成就了一套衣服!','//imgwx4.2345.com/dypcimg/img/a/22/sup66157_223x310.jpg',NULL,NULL,'131',4,1),
  (79,'香港大营救','1941年底，日军占领香港。为实现文化控制，日军全城搜捕滞港的中方文化名人。在中共主持下，东江游击队队长叶伟强进入戒备森严的香港，单枪匹马营救邹涛、蔡楚生等诸多文化名人，完成这不可能完成的任务。','//imgwx4.2345.com/dypcimg/img/c/66/sup198666_223x310.jpg',NULL,NULL,'94',4,1),
  (80,'赌神3：少年赌神','在六八年的夏天，一名姓高的纱厂东主，因赌成祸，引至家破人亡。他的八岁儿子高进从此丧失家园，加入了老千集团跟千王靳能（钟景辉）学一切千门技术，十七岁的高进（黎明）已成为独当一面的千门高手，并且与靳能的女儿靳轻（梁咏琪）情投意合，可谓春风得意。不料风云突变，在一次「赌神」大赛中高进被自己最信任的师父出卖，并下毒手枪击，高进头部中弹，生命垂危，幸好好友七姑娘（袁咏仪）与龙五（陈小春）伸手相助，才得免一死，痊愈后的高进才知道自己的师兄高傲（吴镇宇）已取得了赌神的称号，并且与靳轻结婚，而自己成为了一件牺牲品，在连番的打击下，高进振作精神，决定与师兄及靳能在赌场上决一死战，取回属于自己的东西，成为真正的“','//imgwx3.2345.com/dypcimg/img/3/0/sup316_223x310.jpg',NULL,NULL,'100',4,1),
  (81,'极速60秒','孟菲斯是这个世界上最高明的窃车贼，人们形容他为“gone in 60 seconds”，即只要他盯上的车停在这里，他60秒内便能连人带车一起消失得无影无踪。孟菲斯终究厌倦了窃车贼的虽然惊险刺激但重压在身的生活，宣布金盘洗手 了。但他的弟弟却想步其后尘，不料很快就卷入了一次黑帮危险的交易中。孟菲斯为了救出兄弟，只好重操旧业，答应黑帮一晚内偷来50辆车。 不料，警察正在密切监视着孟菲斯。当他偷得最后一辆野马跑车时，警车、警察直升机尾随而至，展开了一场马路追逐……','//imgwx3.2345.com/dypcimg/img/6/13/sup40423_223x310.jpg',NULL,NULL,'99',4,1),
  (82,'极限探险','他们将横越摩洛哥国度，从Merzouga开始，终点是Marrakech。整个比赛路程长近2,000英里，途径撒哈拉大沙漠和阿特拉斯山脉，而队员们也将“在穿越沙漠、山川和河流的同时解决问题”。','//imgwx2.2345.com/dypcimg/img/f/50/sup150796_223x310.jpg',NULL,NULL,'103',4,1),
  (83,'特种保镖','身为特战队员的张天择在一次缉毒任务“驱魔行动”中误伤了自己的战友，陷入深深自责的张天择选择离开特战队。机缘巧合成为了富豪千金小雅的贴身保镖，两人渐渐成为了忘年交。但好景不长，曾经的犯罪团伙余孽绑架了小雅，并要挟张天择二十四小时内必须将毒品红粉骷髅的配方磁盘找到并交出来。张天择能不能找到红粉骷髅磁盘？能不能成功解决小雅？一场特战风暴即将拉开序幕。','//imgwx5.2345.com/dypcimg/img/4/65/sup196744_223x310.jpg',NULL,NULL,'70',4,1),
  (84,'冰雪女王3:火与冰','不断遇到麻烦可算是凯与格尔达这对姐弟的特点了，毕竟他们是在冰雪的国度被矮人们养大的。随着他们长大成人，等待他们的麻烦也升级了。这一次，格尔达与新认识的小伙伴罗兰去寻找传说中的许愿球，不想却召唤出封印在许愿球中的邪恶力量，这对姐弟是否能够逢凶化吉，还世界一个和平？','//imgwx4.2345.com/dypcimg/img/9/66/sup198932_223x310.jpg',NULL,NULL,'89',4,1),
  (85,'杀手之王','一名老人在中日战争时期被日本财阀杀害了全家，故此他请了世界第一杀手刺杀财阀。财阀被杀后，他生前设立的复仇基金启动了，查出并杀死幕后指使者的人能得到一亿基金。全世界的杀手都对这笔基金虎视眈眈，就连财阀的孙子都找来世界第二号杀手灰狼寻找真凶。鳄佬与小富是杀手群中的一对拍档。身受不凡的小富希望能赚钱回老家，刚好被骗子鳄佬利用了他的身手，两人合作致力得到高额基金......','//imgwx2.2345.com/dypcimg/img/0/7/sup21765_223x310.jpg',NULL,NULL,'104',4,1),
  (86,'父子雄兵','梦想靠大项目咸鱼翻身的范小兵（大鹏 饰）始终无法摆脱一个阴影，那就是退伍军人出身的老爹范英雄（范伟 饰）。小兵油嘴滑舌爱忽悠，英雄刚正不阿会武术，小兵斗英雄，天雷勾地火。卤煮店小老板刘雯（张天爱 饰）与小兵相识十年，两人友达之上恋爱未满，她是小兵唯一愿意吐露真心的港湾。范小兵欠下大老板OK哥（任达华 饰）的钱，被追债小弟方健（乔杉 饰）一路折磨，倒霉不断，还面临凶狠狗哥（梁龙 饰）的生命威胁。范小兵不得已将弄钱对象转向老爹，不知情的范英雄此时正躲闪着邻居邬仙仙（邬君梅 饰）如狼似虎的猛烈追求，殊不知儿子范小兵已经针对自己开启了一个搞笑又荒唐的惊天计划','//imgwx5.2345.com/dypcimg/img/1/63/sup191420_223x310.jpg',NULL,NULL,'105',4,1),
  (87,'疯狂动物城（国语版）','疯狂动物城是一座独一无二的现代动物都市。每种动物在这里都有自己的居所，比如富丽堂皇的撒哈拉广场，或者常年严寒的冰川镇。它就像一座大熔炉，动物们在这里和平共处――无论是大象还是小老鼠，只要你努力，都能在此闯出一番名堂。不过乐观的警官兔朱迪（金妮弗・古德温 Ginnifer Goodwin 配音）却发现，作为史上第一任兔子警官，要和一群强硬的大块头动物警察合作可不是件容易事。为了证明自己，她决心侦破一桩神秘案件；追寻真相的路上她被迫与口若悬河、谎技高超的狐尼克（杰森・贝特曼 Jason Bateman 配音）联手，却发现这桩案件背后隐藏着一个意欲颠覆动物城的巨大阴谋……','//imgwx1.2345.com/dypcimg/img/a/53/sup161388_223x310.jpg',NULL,NULL,'102',4,1),
  (88,'傲娇与偏见','北漂网络写手唐楠楠脑洞大于常人，整日穿行在各种“白日梦”之中，梦想着有一天能成为网文大神，然而在她误打误撞毁掉了贱萌海龟男朱侯为追求女神精心布置的告白仪式之后，这对冤家阴差阳错之下被迫带着各自的傲娇与偏见开始了同居生活。朱侯的高富帅好基友萧见君因对唐楠楠一见钟情展开猛烈攻势，却不料遭到朱侯再三阻挠。当“傲娇”任性撞上“偏见”无理，一场鸡飞狗跳的脱线恋情正在袭来，一次跨越银河系的世纪大和解即将上演。','//imgwx2.2345.com/dypcimg/img/5/63/sup191232_223x310.jpg',NULL,NULL,'107',4,1),
  (89,'妈妈咪鸭','小黄鸭大电影《妈妈咪鸭》讲述了雁群往南方迁徙的途中，“最棒飞行家”大鹏桀骜不驯爱捣乱，和他的女友晶晶调情时不慎撞散了一群赶路的小鸭子。不服管教的大鹏决定单飞，不得已和两只落单的小鸭子淘淘、��组成了“临时父子档”，踏上了一路乌龙的寻亲和南迁之旅。他们经历了一连串惊险、刺激又有趣的冒险。划过壮美的梯田，爬过冷峻的山脉，遇上好心收留他们的母鸡和妻管严公鸡、搭乘春运卡车的猪群、半瓶子中医技能的松鼠。路途中要时刻抵御想吃掉他们的恶猫，还险些成为“盘中烤鸭”。大鹏与淘淘姐弟两的命运紧紧关联，孤傲的大鹏也逐渐学会了责任与耐心。最后，焕然一新的大鹏得到了女友晶晶的原谅，和两只小鸭子组成了爱意满满的小家庭。','//imgwx5.2345.com/dypcimg/img/0/65/sup195766_223x310.jpg',NULL,NULL,'',4,1),
  (90,'猩球崛起2：黎明之战','十年前，人类为自己的愚蠢付出惨痛代价，凶险致命的猿流感病毒蔓延全球，世界毁灭殆尽。而在毗邻旧金山的原始丛林，凯撒（安迪・瑟金斯 Andy Serkis 饰）带领猩猩伙伴们建立起无忧无虑的王国，直到某一天，猩猩的家园的宁静再度被人类打破。在旧城苟延残喘的人类面临能源危机，他们试图利用森林腹地大坝的发电站提供能源。青年科学家马尔科姆（杰森・克拉科 Jason Clarke 饰）试图说服聚集地负责人德里弗斯（加里・奥德曼 Gary Oldman 饰），希望通过和平手段取得猩猩部落的信任和同意。然而凯撒的好盟友科巴（托比・凯贝尔 Toby Kebbell 饰）却无法摒除对人类的憎恨，他尝试着挑战者凯撒','//imgwx5.2345.com/dypcimg/img/2/42/sup127624_223x310.jpg',NULL,NULL,'130',4,1),
  (91,'大梦西游4伏妖记','西行取经人唐僧与孙悟空在渡人和降妖孰难孰易的问题上发生了争执，孙悟空使用移形换影大法把自己的法力给了唐僧，而自己变成了学识渊博却毫无法力的帅哥。移形换影大法只得维持七日，两个人便以七日为限，分别去渡化盘丝洞的蜘蛛七恶女和降服黄花观的百目魔君.........','//imgwx4.2345.com/dypcimg/img/4/65/sup196699_223x310.jpg',NULL,NULL,'99',4,1),
  (92,'千与千寻（国语）','有点娇气任性的10岁少女千寻跟随父母搬往新家，途中误入一座神庙，来到了另一个世界――一条专门给神仙提供服务的热闹非凡的浴场街。双亲因为吃了给神准备的美食而被变成了猪。小千寻在迷之美少年白的帮助下得以安身，改名为“千”，进了魔女汤婆婆经营的大浴场做帮佣，等待机会救出父母回到自己的世界。在这里有口恶心善的烧锅炉的爷爷，有性格爽利热心肠的玲大姐，在大家的激励下，千寻以自己的纯真之心屡屡化险为夷，并交到了许多新朋友。最终，千寻拯救了白，发现了他的真实身份。然而要救出双亲，她还必须面临严酷的考验……','//imgwx4.2345.com/dypcimg/img/f/56/sup169749_223x310.jpg',NULL,NULL,'124',4,1),
  (93,'第一滴血4','军政府统治下的缅甸，其西部由于蕴含丰富资源而被军队觊觎多年，那里的儿童经常被军队强征入伍，针对平民的屠杀已经成了部分军人的娱乐……一支教会义工小队为了进入该地区向难民提供人道主义帮助，在泰国找到了隐居的兰博（西尔维斯特史泰龙 Sylvester Stallone 饰），希望熟悉河道的后者提供帮助。兰博认为时局没有改变的可能，拒绝带路，但终被义工莎拉（Julie Benz 饰）的诚意打动，将他们送达。\r\n岂料这支义工小队在村庄中遭到军队的袭击，生还者全部被掳走。教会获悉义工们身陷险境，组织雇佣兵营救，并再次委托兰博带路，这一次，兰博的态度发生了转变，他率领雇佣兵们潜入缅甸军营，又一次化身热血战士','//imgwx1.2345.com/dypcimg/img/0/0/sup1195_223x310.jpg',NULL,NULL,'91',4,1),
  (94,'欲望（2002）','长久以来，罗莎（张昭妍 饰）和库敏（安泰健 饰）都享受着平静而幸福的婚姻生活，可是，就在最近，罗莎发现丈夫的行踪有些诡异。心生疑虑的罗莎开始怀疑丈夫在外面有了情人，被妒忌折磨的痛苦不堪的罗莎决定靠自己的力量找出这个隐匿在丈夫背后的第三者。通过跟踪，罗莎终于目睹了这位第三者的真容，然而，神秘面纱的揭开却让罗莎开始后悔起自己鲁莽的举动来。丈夫的情人并非什么美艳少妇，却是一个姿态妖娆的男妓，一时间，罗莎不知如何是好。渐渐的，罗莎发现男妓的身影占据了自己的全部大脑，自己居然在莫名之间深深的被他吸引。千回百转之后，两人终于越过了那道禁忌的界线，至此，剧情急转直下，两男一女该如何处置他们泛滥的欲望和情感呢','//imgwx1.2345.com/dypcimg/img/d/29/sup88874_223x310.jpg',NULL,NULL,'78',4,1),
  (95,'运动学概论','该片根据人气网络小说衍生而成，在北京大学取景拍摄，讲述了社交恐惧症的孤独学霸、四肢发达的没头脑体特和古灵精怪的偏执校花之间的友情与爱情故事。','//imgwx3.2345.com/dypcimg/img/1/66/sup198578_223x310.jpg',NULL,NULL,'75',4,1),
  (96,'金装少年唐伯虎','少年唐寅出生在一个豪富之家，无奈老爸老妈过于信奉“穷养儿，富养女”的人生理念，故意装作穷苦人家，以期待唐寅能够“健康成长”，成为可造之材并将唐寅送入一个文武同授的书院中。可是书院却被恶势力要挟，唐伯虎师徒二人经过一番波折，终于联手并在对决中惊险获胜，将恶势力击退。','//imgwx3.2345.com/dypcimg/img/c/66/sup198060_223x310.jpg',NULL,NULL,'82',4,1),
  (97,'女教授的隐秘魅力','兼任大学染色系教授和环境组织“绿色21”一员的魅力女教授恩淑，是一位兼备智慧与美貌的女人，并且拥有具社会地位。她的魅力让周围的男人无法抵挡，以至于这些男人用尽一切办法来对她展开爱情攻势。','//imgwx4.2345.com/dypcimg/img/7/7/sup21772_223x310.jpg',NULL,NULL,'91',4,1),
  (98,'钢铁侠2','钢铁侠托尼・斯塔克在国会听证上拒绝交出最新技术。与此同时，他发现胸口的微型电弧反应炉正迅速造成血液的钯金属中毒。沮丧的托尼将斯塔克公司的总裁职务交予了秘书波兹，由她全权负责正在进行的纽约斯塔克博览会。波兹从法律部门调来助理娜塔莉照顾托尼。托尼在媒体前的高调亮相引起了其父当年同事的儿子，伊凡的不满。为了实施报复，他子承父业，研制出了一套可与钢铁战衣相媲美的装备。伊凡的技术引起了托尼的竞争对手，军火商贾斯丁・汉默的注意，他设法将伊凡劫持出狱，秘密研究取代钢铁侠。正当托尼苦于钯金属中毒造成的失意之时，他发现娜塔莉原来身负秘密使命，而自己的任性，也造成了与好友，空军上校罗尼的反目，眼看局势就要失控……','//imgwx5.2345.com/dypcimg/img/6/5/sup17500_223x310.jpg',NULL,NULL,'124',4,1),
  (99,'莫斯科陷落','　难得一见的流星雨即将在俄罗斯上空出现，青年男女们兴奋非常，纷纷走上街头见证着历史性的一刻。美丽女孩尤利娅不顾身为军官的父亲的反对，执意和特姆交往。在流星雨来的这天，他们躲在一隅偷欢，却不知莫大的危机正在袭来。一艘外星飞船遭到意外，结果又被俄罗斯空军击中。它在迫降的过程中给莫斯科造成毁灭性的打击，而尤利娅的闺蜜也在灾难中丧生。在此之后，尤利娅和朋友们闯入禁区，遭遇了外星人。阴差阳错，她决定救治受重伤的外星飞行员汉克，而特姆等憎恶外星人带来的灾难，对汉克恨之入骨。','//imgwx4.2345.com/dypcimg/img/5/66/sup198577_223x310.jpg',NULL,NULL,'117',4,1),
  (100,'江湖学院','本片讲述物欲横流的现代都市生活中，一个平民英雄浩哥为了情和义在都市中辗转和打拼的动人故事。阿译因二龙湖暗藏的巨大商机接近张浩，并送给张浩一箱买猪钱。张浩拿着钱开始了二龙湖生态养猪场的创业，并经营得有声有色，带着二龙湖村民走上了致富之路。然而，张浩在陈国富和阿译的利诱下得意忘形，与身边真正关心他的兄弟以及蓝莓纷纷决裂。在得知陈国富的阴谋和欺骗后，张浩陷入了众叛亲离的境地，在蓝莓的帮助下，四兄弟重聚，支持张浩成功反抗了陈国富的黑帮势力。','//imgwx4.2345.com/dypcimg/img/f/65/sup197917_223x310.jpg',NULL,NULL,'',4,1),
  (101,'花芯','在父母亲的安排之下，园子（村川绘梨 饰）嫁给了名为雨宫（林遣都 饰）的青年，从此告别了少女时代，步入了家庭主妇的行列。然而，园子的内心里对于雨宫没有丝毫的感情，她的丈夫对于她来说，不过是和自己住在同一屋檐下的陌生人而已。即便如此，园子还是和雨宫发生了肉体关系，并且怀上了雨宫的孩子。十个月之后，园子成为了母亲。 之后，工作上的变动让雨宫一家人来到了京都生活。一次偶然中，园子结识了雨宫的上司越智（安藤政信 饰）。在一来二去的交往之中，园子被风度翩翩温文尔雅的越智所吸引，而越智亦在园子的身上发现了寂寞的气息，最终，两人越过了伦理道德的那条界限。','//imgwx4.2345.com/dypcimg/img/c/65/sup195436_223x310.jpg',NULL,NULL,'78',4,1),
  (102,'冰雪奇缘','在四面环海、风景如画的阿伦达王国，生活着两位可爱美丽的小公主，艾尔莎和安娜。艾尔莎天生具有制造冰雪的能力，随着年龄的增长，她的能力越来越强，甚至险些夺走妹妹的生命。为此国王紧闭宫门，也中断了两个女儿之间的联系。悲哀的海难过后，艾尔莎（伊迪娜・门泽尔 Idina Menzel 配音）终于到了加冕的年龄，各国王宫纷纷前来祝贺。艾尔莎皇后战战兢兢，唯恐被人识破隐藏了多年的秘密。然而当听说安娜（克里斯汀・贝尔 Kristen Bell 配音）将要和初次见面的南群岛王子汉斯（圣蒂诺・方塔纳 Santino Fontana 配音）结婚时，依然情绪失控露出了马脚。在此之后她逃到山中，构建了属于自己的冰雪王国','//imgwx1.2345.com/dypcimg/img/4/35/sup105621_223x310.jpg',NULL,NULL,'101',4,1),
  (103,'赛尔号大电影6：圣者无敌','　从前，在神秘森林里有个外星小孩儿，他带着面具，机灵古怪，终日与吹泡泡的毛猴儿们为伴。 \r\n　　某日，一群凶残至极的鲨齿狼冲进森林，只见头狼身上坐着宇宙精灵捕手克拉，与正在寻找圣者谱尼的SPT队员们撞个正着。 \r\n　　一场上天入地的激战就此展开！ \r\n　　一路上SPT队员们偶遇食人鲨、野猪、圣光彩蝶、闪光比波、利牙鱼、蘑菇怪…… \r\n　　孰敌孰友？他们能顺利找到谱尼，开启赛尔号的下个征程吗？','//imgwx1.2345.com/dypcimg/img/0/64/sup193929_223x310.jpg',NULL,NULL,'20',4,1),
  (104,'毒蛇（2008）','　 隶属环球生物公司的科学家维拉科林斯正在进行一项危险的试验，她通过改造、放大毒蛇体内的毒性，然后提取所需要的物质以治疗癌症。但令其始料未及的是，这些蛇也因此变成了可怕的掠食者。　　某天噩梦终于降临，基因突变的毒蛇从研究所逃出，它们一路奔袭，吞噬一切可以下咽的食物，人类和动物全在其掠食范围之内。附近的村庄因此遭殃，侥幸逃生的尼姬和汉克必须想方设法从这个已经被恐怖毒蛇占领的小岛上逃出……','//imgwx1.2345.com/dypcimg/img/2/50/sup150779_223x310.jpg',NULL,NULL,'86',4,1),
  (105,'芳华','影片讲述了在充满理想和激情的部队文工团，一群正值芳华的年轻人在爱情萌发时经历了残酷战争的洗礼。“活雷锋”刘峰（黄轩饰）由于一次触摸事件被集体抛弃，从农村来的何小萍（苗苗饰），因“不良习气”屡次遭到文工团女兵的歧视与排斥，萧穗子（钟楚曦饰）默默暗恋着文工团小号手。三人因命运巧合重逢在硝烟弥漫的战场，昔日文工团的战友林丁丁、郝淑雯、陈灿等人也在时代变革中走上了截然不同的人生命运。','//imgwx5.2345.com/dypcimg/img/1/63/sup189593_223x310.jpg',NULL,NULL,'135',4,1),
  (106,'赌神','赌神高进(周润发)约了”赌魔”陈金城(鲍汉琳)一个月后决战公海。　　 　　高进刚抵香港，即与南哥(杨泽霖)交手，南哥指使越南杀手追杀高进。刀仔(刘德华)与女友珍(王祖贤)阴差阳错下，救了误堕陷阱而失忆之高进。刀仔发现进精通赌术，利用其天份�钱。　　 　　刀仔将高进搬回家中护理。进妻Janet(张敏)见进失踪多日，欲报警却被进堂弟高义(龙方)阻止，当晚更醉酒将Janet奸杀。　 　　另方面，高义决与陈金城合作将高进杀死，得知进藏身地点后一起追杀!龙五出现带众人逃走，进被的士撞倒送医救治，渐回复记忆，但忘记失忆时发生的事。刀仔欲与之相认及告知义乃不怀好意，却反遭毒打。　 　　决战当日，陈金城三人设','//imgwx4.2345.com/dypcimg/img/9/0/sup365_223x310.jpg',NULL,NULL,'91',4,1),
  (107,'狂怒之阿登战役','1944年末，纳粹党发动了一场战役，一支美国坦克部队被困在敌人后方。盟军行动阿登愤怒即将开始，当坦克指挥官移动到附近的孤儿院，拯救无辜的孩子们。','//imgwx1.2345.com/dypcimg/img/4/64/sup193638_223x310.jpg',NULL,NULL,'87',4,1),
  (108,'正义联盟','受到超人无私奉献的影响，蝙蝠侠重燃了对人类的信心，接受了新盟友――神奇女侠（戴安娜・普林斯）的帮助，去对抗更加强大的敌人。蝙蝠侠和神奇女侠一同寻找并招募了一支超人类联盟来抵挡新觉醒的威胁。但尽管这支队伍集结了超人、蝙蝠侠、神奇女侠、闪电侠、海王和钢骨等人，他们似乎无法阻止敌人对地球的进攻','//imgwx1.2345.com/dypcimg/img/6/65/sup195410_223x310.jpg',NULL,NULL,'20',4,1),
  (109,'钢铁侠','斯塔克军火公司是美军在全球范围内第一大军火供应商，其新任掌门人托尼斯塔克（小罗伯特・唐尼 Robert Downey Jr. 饰）风流倜傥，天资聪颖。他与公司元老俄巴迪亚斯坦（杰夫・布里吉斯 Jeff Bridges 饰）合作无间，共同将斯塔克公司的业务推向顶峰。现 实生活中的托尼热衷收集名贵跑车，搞点儿发明创造，当然露水姻缘更不可少。所幸他身边有维吉尼亚波茨（格温妮斯・帕特洛 Gwyneth Paltrow 饰）这样的好助手细心打理一切，才让他能自由自在过着贵公子的生活。\r\n在前往中东为军方展示新型武器的途中，托尼一众遭到恐怖分子袭击。他被弹片击中险些丧命，在英森博士的帮助下，托尼体内移植了','//imgwx3.2345.com/dypcimg/img/5/13/sup39171_223x310.jpg',NULL,NULL,'125',4,1),
  (110,'神奇女侠','《神奇女侠》由女导演派蒂・杰金斯(Patty Jenkins，代表作品《女魔头》)执导，盖尔・加朵、克里斯・派恩(Chris Pine，代表作品《星际迷航》系列)等好莱坞当红明星主演。故事讲述加朵饰演的亚马逊公主戴安娜・普林斯，经过在家乡天堂岛的训练，取得上帝赐予的武器与装备，化身神奇女侠，与克里斯・派恩饰演的空军上尉史蒂夫・特雷弗一同来到人类世界，一起捍卫和平、拯救世界，在一战期间上演了震撼人心的史诗传奇。','//imgwx1.2345.com/dypcimg/img/6/11/sup35616_223x310.jpg',NULL,NULL,'135',4,1),
  (111,'机器之血','《机器之血》是一部由成龙主演并担纲监制的动作片，由成龙、罗志祥、欧阳娜娜、夏侯云姗联袂出演。讲述了林东为配合证人保护计划卷入了一件与自己无关的案件，成为了无法掌控的巨大阴谋之下的牺牲品。 多年以后，伴随着科幻小说《机器之血》的出版，当年卷入争斗的各方纷纷浮出水面。','//imgwx3.2345.com/dypcimg/img/f/60/sup180807_223x310.jpg',NULL,NULL,'95',4,1),
  (112,'哈利波特1：魔法石','哈利波特是一个孤儿，从小寄养在姨妈家，受尽欺凌。但就在哈利11岁生日的时候，他意外收到了霍格沃茨学院的入学通知书。哈利从该学院派来接他的巨人海格口中得知，这是一间魔法学院，并得知了自己的身世，原来哈利的父母都是伟大的魔法师，在对付伏地魔的战斗中双双献身，唯有哈利幸免于难。　　哈利进入霍格沃茨后，表现出了超乎想象的飞行天赋，得到麦格教授的推荐进入了格兰芬多的魁地奇球队。另一方面，哈利发现霍格沃茨学院内有一股黑暗势力似乎在暗暗滋长，揭开谜团的关键就在有凶恶的三头犬守护的房间内。　　哈利、罗恩和赫敏三个好朋友决定探个究竟。','//imgwx3.2345.com/dypcimg/img/c/53/sup161133_223x310.jpg',NULL,NULL,'158',4,1),
  (113,'银河护卫队2','银护正受雇于大祭司阿耶莎去保护她治下的至高星人，这是一个全身金色的人类种族。作为回报，阿耶莎将他们的囚犯星云送给了银河护卫队。然而关键时刻火箭浣熊玩起双面间谍的游戏，背地里窃走至高星的能量电池，致使阿耶莎发动全军追杀银河护卫队，英雄们在神秘人物伊戈的帮助下惊险逃生，而伊戈正是星爵的亲生父亲，于是星爵、卡魔拉与毁灭者就追随伊戈和他的伙伴螳螂女前往他的家园。与此同时，阿耶莎雇佣了勇度的掠夺者团队继续追杀银河护卫队，火箭、格鲁特宝宝和星云却在试图修复飞船时与勇度狭路相逢。星云利用太空海盗帮内不合趁机夺取了勇度飞船的控制权，但勇度、格鲁特宝宝与火箭浣熊最终顺利脱逃，与其余银河护卫队成员汇合。星爵渐渐意','//imgwx4.2345.com/dypcimg/img/6/49/sup149444_223x310.jpg',NULL,NULL,'130',4,1),
  (114,'生化危机1：变种生还','为军方研究生化武器的安布雷拉公司在浣熊市地下设有巨大的研究中心――蜂巢，由于一次意外事故导致可通过空气传播的生化武器泄露，负责蜂巢安保的电脑系统“红色女王”启动应急措施，却导致数百名工作人员罹难……四小时后，蜂巢安保人员爱丽丝（米拉・乔沃维奇 Milla Jovovich 饰）在蜂巢出口醒来，并且因为催眠气体而失忆。一支受命关闭红色女王的特种小队来到此处，与爱丽丝、自称警察的安德森，以及蜂巢工作人员帕克斯组队深入地下。一行人因任务目标与红色女王冲突而先后遭遇保卫系统和丧尸的连番袭击，而爱丽丝渐渐恢复的记忆揭示了她与同行者非同一般的联系。这场生化丧尸危机该如何解除？又或者根本是一曲绝望的前奏？\r','//imgwx2.2345.com/dypcimg/img/4/20/sup62340_223x310.jpg',NULL,NULL,'100',4,1),
  (115,'死神来了3','高中生与友人一起去游乐场庆祝毕业，大家玩得十分痛快。游乐场内灯火璀璨，却诡异处处。在坐上过山车的座椅时，Wendy忽然看到了怪异现象，心生恐怖，男友Jason安慰她不过是幻觉。过山车开动起来，可是在半空中突然出轨，大家倒挂空中。Wendy眼看着朋友一个一个坠落死亡，自己也不能幸免于难。被同学的吆喝喊醒的Wendy惊觉刚才不过是幻觉，但是她忧心忡忡，力阻大家离开过山车。','//imgwx2.2345.com/dypcimg/img/2/54/sup163266_223x310.jpg',NULL,NULL,'93',4,1),
  (116,'侏罗纪公园2','当年在努布拉岛营建侏罗纪公园时，约翰.哈蒙德（理查德.阿滕伯勒 Richard Attenborough 饰）曾把附近的索纳岛作为恐龙的制造工场。侏罗纪公园沦陷后，索纳岛上的恐龙在完全隔绝且缺少必要合成元素的情况下生存了4年时间。哈蒙德对此颇感好奇，他重新找到马科姆博士（杰夫.高布伦 Jeff Goldblum 饰），邀请他前往小岛考察。4年前经历让马科姆心有余悸，他断然拒绝哈蒙德的提议，但当得知女友莎拉（朱丽安.摩尔 Julianne Moore 饰）已前往小岛之时，只得跟随探险队整装出发。　　在索纳岛上，另一组野蛮的探险队已经先期到达，他们抓捕恐龙将其买到外国。利欲熏心使得这些贪婪之徒全然','//imgwx1.2345.com/dypcimg/img/a/10/sup31963_223x310.jpg',NULL,NULL,'128',4,1),
  (117,'追龙','　电影《追龙》讲述了香港现代史上两大著名狠角色大毒枭跛豪（甄子丹饰）、五亿探长雷洛（刘德华饰）的传奇故事。 \r\n　　上世纪六七十年代，香港由英国殖民，权势腐败、社会混乱。1963年，穷困潦倒的青年阿豪（甄子丹饰）偷渡至香港，抱着“生死有命、富贵在天”之心决意一搏人生。阿豪带着几个兄弟，从九龙城寨底层开始一路刀刃舔血，爬上香港毒品霸主之位，一手掌控香港十大黑帮，江湖人称“跛豪”。而雷洛（刘德华饰）则从一位普通探长一步步爬上华人总探长高位，统领全香港三万警察，手握香港治安“潜规则”。为垄断香港黄赌毒三大经济产业，跛豪与雷洛结拜为兄弟，两人一黑一白两手遮天，权势滔天，家财亿万，独霸香港岛','//imgwx2.2345.com/dypcimg/img/9/64/sup194652_223x310.jpg',NULL,NULL,'95',4,1),
  (118,'大唐第一刺局','大唐年间风云变幻，女主武氏欲称帝，建立武周王朝，反对之声四起。坊间传言大相天师袁天罡，曾受命于太宗皇帝，秘密绘制了一张十二天机《推背图》，此图乃是根据堪兴六壬之术，天演推算出了武氏气运之所，用以牵制“莲花生相”的女主武王一说。袁天罡将十二天机推背图刻于拥有隐幻皮的精绝国王子背部，得知真相的武则天，派出天机道刺客秘密护送精绝王子回宫，表面以两国交好为由，实则欲取图而杀之。天机道刺客误入鬼魅驿站，传说中的转生之术出没江湖，精绝王子不知所踪，迷局未接解。而此时，宫中神秘人却派出地鬼道刺客一同前往寻找推背图，两方杀手在寻找十一皇子的路上，发现彼此陷入了一场连环死局。一场新的厮杀由此展开……','//imgwx1.2345.com/dypcimg/img/9/66/sup198911_223x310.jpg',NULL,NULL,'',4,1),
  (119,'解忧杂货店','改编自东野圭吾原著同名小说《解忧杂货店》，讲述了在一家僻静街道旁的杂货店，只要写下烦恼投进店前门卷帘门的投信口，第二天就会在店后的牛奶箱里得到回答。某个夜晚，三个身在迷途的年轻人阿杰、小波、彤彤无意闯入，没想到开启了时光隧道，从收到来自过去的第一封咨询信开始，在不断的回信和收信过程中，过去链接了未来，犹豫是否该坚持音乐梦想的秦朗、是否该和家人因逃债远走他乡的浩博、是否应该放弃舞女工作的晴美都从他们的回信建议做出了选择，找到了心灵的方向，不经意间也串联了所有人的人生。同时这三个年轻人也收到了对于自己困惑的解答。','//imgwx1.2345.com/dypcimg/img/0/64/sup193717_223x310.jpg',NULL,NULL,'108',4,1),
  (120,'奇幻森林','《奇幻森林》是一部全新的迪士尼真人电影，主角是一个狼群养大的人类小男孩毛克利(新人演员尼尔・塞西)，影片则围绕他波澜壮阔的大冒险展开。当被迫要放弃他仅有的森林之家时，毛克利踏上了一场精彩纷呈的自我探索旅途。','//imgwx2.2345.com/dypcimg/img/8/52/sup158165_223x310.jpg',NULL,NULL,'105',4,1),
  (121,'试戏','一心做演员梦的女孩儿心弦离家北漂。同租的仙女姐告诫她做演员演床戏是不可避免的，要学会吃糖衣吐炮弹。心弦到处试戏，苦恼于导演们的咸猪手，却得知仙女姐的吃糖衣另有苦衷。在经纪人张微信“为艺术献身”的蛊惑下，心弦穿上比基尼和导演试戏却再遭咸猪手。万念俱灰的心弦哭着点燃自己珍爱的表演笔记，发誓再不当演员。与她有过一面之缘的外卖小哥“充电器”给她介绍了送外卖的工作，当她送外卖偶遇星探时，她又做起了演员梦，却再次受骗。心灰意冷的心弦彻底放弃演员梦想，却遇到了真的大导演，并歪打正着演了一个小角色，正在庆幸之际，副导却带话让心弦到导演房间谈戏，说导演看中她要给她加戏……','//imgwx2.2345.com/dypcimg/img/1/59/sup179766_223x310.jpg',NULL,NULL,'72',4,1),
  (122,'极品飞车','纽约基斯科山，小青年托比・马歇尔 和伙伴们共同经营一家改装车行，他同时也是一名技术精湛的赛车高手。虽然刚刚在一场比赛中赢得奖金，但是车行的经营状况还是到了令人堪忧的底部。恰在此时，拥有专业赛车手资格的土豪迪诺・布鲁斯特 找上门来。迪诺此前撬走了托比的女友，两人可谓不共戴天。但是迪诺此次给他带来一单不小的改装生意，托比和伙伴们成功打造出一辆时速超过370公里的福特野马，而嫉妒心炽盛的迪诺为了最强赛车手的名号向托比发起挑战。为了丰厚的报酬，托比和好友利托・比特共同参赛。谁知迪诺导致利托死亡，事后还嫁祸托比。出狱后，托比借来他的那辆得意之作福特野马，发誓要为利托讨回公道……','//imgwx2.2345.com/dypcimg/img/8/34/sup103138_223x310.jpg',NULL,NULL,'124',4,1),
  (123,'全球风暴','故事设定在未来世界，人类已经拥有了通过卫星来操控天气的科技，从此极端天气成为历史。然而，数颗卫星却突现出现故障，原本应该保护地球的卫星系统开始攻击地球，一场席卷整个世界、摧毁一切的灭世浩劫从天而降。杰拉德・巴特勒在电影里饰演一位名叫杰克的科学家，吉姆・斯特吉斯则饰演他的弟弟麦克斯，两人要争分夺秒，在地球被毁灭殆尽之前修复气象卫星。兄弟俩一个在天上操控卫星，一个在地面指挥工作，紧密合作；在大气层外的杰克将遭遇空间站毁灭、卫星爆炸等艰难险阻，在地面上的麦克斯更要绑架总统，窃取国家机密才有机会力挽狂澜。','//imgwx5.2345.com/dypcimg/img/a/64/sup194650_223x310.jpg',NULL,NULL,'20',4,1),
  (124,'奇异博士','漫威影业此次花大手笔制作电影《奇异博士》，对该片寄予厚望。日前漫威的执行总裁对好莱坞记者说道，“《奇异博士》将会打破我们之前已经成文的标准，我们对本片形象孕育良久，他不同于我们之前做的任何形象。”漫画中的“奇异博士”曾是一名神经外科医生，会使用威力强大的魔法。他能用魔法飞行、刀枪不入、受到攻击后能自动恢复，还能制造水和食物与极具杀伤力的武器，甚至能召唤亡灵，是一位法力无边的巫师。','//imgwx2.2345.com/dypcimg/img/7/49/sup149441_223x310.jpg',NULL,NULL,'110',4,1),
  (125,'木乃伊3','1947年，中国宁夏。中国两千年前的始皇帝：龙帝之墓在此挖掘出土。同时在美国，大探险家欧康诺（Brendan Fraser饰）与夫人伊芙琳（Maria Bello饰）应邀，将传说中能带来永生的宝石“香格里拉之眼”送往中国，以示中美两国人民友谊长存。　　不料，国民党军官（黄秋生饰）却和博物馆馆长勾结，当场抢走香格里拉之眼，继而唤醒了两千年前的邪恶力量――龙帝（李连杰饰），此时的龙帝不仅可以借助五行之力施暴，还可以变身为各种怪物涂炭天下生灵。　　欧康诺一家奋力与龙帝和他身后庞大的兵马俑队伍展开战斗，千年圣女紫媛（杨紫琼饰）与其女林娜（梁洛施饰）也入盟此支神圣的队伍，以期消灭龙帝，还世界以和平。','//imgwx5.2345.com/dypcimg/img/9/17/sup52966_223x310.jpg',NULL,NULL,'111',4,1),
  (126,'隐秘的诱惑','讲述的是一个女子受到诱惑希望能一夜改变自己的人生，不顾危险登上了澳门最高级的豪华游艇，和两个男人之间产生了爱和欲望的故事。林秀晶在片中饰演被朋友背叛，被债主追债的女主人公智妍，她为了能摆脱困境而接受了一个看似可以改变人生的提议，却陷入了危险的欲望之中。柳演锡饰演出现在智妍面前的神秘男人，和智妍在澳门第一次相遇之后就牵绊在一起，之后两人之间就有一种暧昧的紧张感，关系走向令人非常好奇。翻拍自肖恩・康纳利1960年代的作品 Woman of Straw，，讲述了一段因心爱的男人的请求投入他人怀抱，最终以悲惨结局收场的悲情罗曼史。','//imgwx1.2345.com/dypcimg/img/c/48/sup146962_223x310.jpg',NULL,NULL,'110',4,1),
  (127,'新龙门客栈','宦官专权的明朝末年，东厂曹少钦（甄子丹）假造圣旨杀害了忠良杨宇轩，并想斩草除根杀死其后代。侠女邱莫言（林青霞）、江湖义士贺虎等人冒死救出忠良后代后，逃至边关沙漠里的龙门客栈，与杨宇轩部下周淮安（梁家辉）会合。龙门客栈实则黑店，老板娘金镶玉（张曼玉）利用乱世，以美貌和风骚作诱饵杀人越货聚敛钱财，但她却为英俊潇洒的周淮安动了真心。为了摸清潜藏于龙门客栈中的出关暗道，周淮安将计就计，答应与金镶玉成亲。\r\n洞房花烛夜，楼上的周淮安与金镶玉各怀心事暗中较劲，楼下的邱莫言、贺虎等人也与东厂一众“走狗”展开了明争暗斗，战事一触即发。豆瓣','//imgwx2.2345.com/dypcimg/img/5/1/sup5721_223x310.jpg',NULL,NULL,'86',4,1),
  (128,'盗墓笔记','落魄作家为了写作素材，寻访到了一个叫做吴邪的古董铺子老板，而吴邪正准备离开这个城市，临走之前，吴邪和他讲诉了关于自己奇怪的盗墓家族往事，并说出了自己第一次随家族探险所经历的诡异事件： \r\n　　那一次他们的家族因为偶然获取了一件特殊的青铜器，追根溯源，寻找到了被掩埋在中国西北盆地中的西王母古国，他们招募了一批盗墓贼一同前往古城遗址探险，进入了位于古城地下的西王母陵中，发现了当年周穆王与西王母求长生不死之术的真相。作家听完吴邪的故事，却发现其中有很多疑点，吴邪到底说的是自己的臆想，还是真相更加可怕复杂，因为吴邪的离开变成了永恒之谜。','//imgwx5.2345.com/dypcimg/img/2/31/sup95841_223x310.jpg',NULL,NULL,'120',4,1),
  (129,'死神来了5','某公司组织一次远游，虽然员工们各怀心事，不过他们还是踏上了欢乐的旅途。在经过一座巨大的吊桥时，年轻人山姆（Nicholas D\\''Agosto 饰）突然看到幻象，幻象中大桥开始垮塌，包括他、同事、朋友在内无数人在这起事故中遇难。当他从幻象中醒来时，周边的种种迹象表明那场灾难即将到来。慌乱之中，他和朱莉（艾玛・贝尔 Emma Bell 饰）、皮特（Miles Fisher 饰）、坎蒂丝（Ellen Wroe 饰）、奥利维亚（Jacqueline MacInnes Wood 饰）等8个人下了车。灾难随即发生，山姆等人成为车上仅有的幸存者。\r\n但是命运并未偏袒这几名幸运儿，死神仍时刻逡巡在他们的周围','//imgwx4.2345.com/dypcimg/img/7/9/sup29108_223x310.jpg',NULL,NULL,'91',4,1),
  (130,'江湖情2：英雄好汉','阿勇（万梓良饰）、阿国（刘德华饰）兄弟俩浪迹香港街头，投靠了黑社会邦主剂哥（周润发饰）。由于阿国得到剂哥赏识，引起阿勇妒嫉，兄弟间产生隔阂。阿勇倒向朱老大，阿国却远去新加坡另谋生计。若干年后，阿勇羽翼丰满，逐杀朱老大自立邦主，又设计将剂哥帮派斩尽杀绝，剂哥遇难，孤身亡命新加坡寻找阿国，阿勇步步追杀，将剂哥妻子（刘嘉玲饰）、儿女惨杀，此刻悲痛、愤恨促使阿国重新出山，随剂哥返回香港以死相拼，报仇雪恨......','//imgwx3.2345.com/dypcimg/img/e/5/sup16576_223x310.jpg',NULL,NULL,'104',4,1),
  (131,'古墓丽影','20年前罗拉（安吉丽娜朱莉 饰）的父亲（乔恩沃伊特 饰）挖掘古墓得到的一个箱里收藏了一个神秘的钟。罗拉从父亲那里知道，这个钟被一个神秘的组织有关。这个叫光明会的组织相信当他们找到这个古钟后便是把时间与空间打开的钥匙，一把三角形的金属钥匙，多年来组织一直苦苦寻觅。\r\n光明会希望通过这把钥匙让他们的组织始祖复活，但首先要找到钥匙。而且时间紧迫，两天后将出现日全蚀的景观，这个时候的三角内在的力量便会到达最大，光明会的不想错过这个大好机会。罗拉扛上了这个艰巨的任务，在重重困难中寻找拯救宇宙的三角。','//imgwx2.2345.com/dypcimg/img/4/8/sup25047_223x310.jpg',NULL,NULL,'100',4,1),
  (132,'速度与激情7 英语','温子仁目前正积极忙于影片拍摄前的准备工作，而且正在设计了很多与众不同的动作场面。除此之外，他还在采访中爆出一段猛料：杰森斯坦森确定加盟《速度与激情7》，饰演第六部大反派欧文肖（卢克伊万斯 饰）的哥哥伊恩肖，届时将上演哥哥为弟弟报仇的戏码。再加上确定回归的范迪塞尔和岩石道恩强森，三大光头猛男无疑将碰撞出更为热血的火花。','//imgwx2.2345.com/dypcimg/img/d/32/sup96048_223x310.jpg',NULL,NULL,'139',4,1),
  (133,'神偷奶爸3','预算并不大、制作时间也不长的影片《神偷奶爸》（又名《卑鄙的我》）自上映起便获得了极大的关注度和良好的口碑，不论是在北美还是海外都获得了超高的票房，这部曾被称为票房黑马的电影成为环球影业可以动画领域的翘楚迪斯尼、梦工厂相互抗衡的第一部电影。它的出现，改变了迪斯尼好莱坞动画电影一枝独秀的局面。如今，本片在经历了5个年头后将于2017年推出该系列的第三部','//imgwx3.2345.com/dypcimg/img/a/54/sup163590_223x310.jpg',NULL,NULL,'20',4,1),
  (134,'斯巴达300勇士','这部电影描述了人类史上最残酷的战争之一：温泉关之战。公元前480年，波斯国王薛西斯一世（罗德里格桑托罗 Rodrigo Santoro 饰）亲率30万大军征战希腊。希腊各个城邦迅速派出军队结成了联军，准备抵御波斯军队的入侵。\r\n防线的最前线设在希腊的温泉关，此处由斯巴达城的国王列奥尼达（杰拉德巴特勒 Gerard Butler 饰）率领本城300精兵和联军4000余人镇守。由于叛徒的出卖，希腊军队被波斯军队抄到了身后形成围剿之势。列奥尼达为了保存联军实力，让联军首先撤退，自己率领300精兵死守温泉关断后。\r\n数以十万波斯大军浩浩商商蜂拥而至，自知必死的斯巴达士兵反而士气高涨，在战斗中爆发出惊人','//imgwx2.2345.com/dypcimg/img/6/17/sup52254_223x310.jpg',NULL,NULL,'116',4,1),
  (135,'侏罗纪公园3','继索纳岛恐龙逃往事件过去已有八年，艾伦格兰特博士（山姆尼尔 Sam Neill 饰）无论如何也不愿再返回那个鬼地方。虽然发下重誓，但现状不得不让他收回誓言。他的一项史前动物研究陷入资金紧张的窘境。而这时，富有的商人保罗科比（William H. Macy 饰）及其夫人阿曼达（Téa Leoni 饰）找到艾伦，邀请他作为向导带他们乘飞机前往索纳岛观光。一方面酬劳颇丰，一方面又得知不用着陆小岛，好了伤疤忘了疼的艾伦接受了他们的邀请。\r\n然而事情却朝相反的方向发展，他们的飞机最终迫降小岛。至此他才得知，原来科比夫妇来此的目的竟是为了寻找失踪的儿子。与此同时他还发现，经过八年的自然进化，恐龙中最聪明的','//imgwx2.2345.com/dypcimg/img/3/10/sup31962_223x310.jpg',NULL,NULL,'92',4,1),
  (136,'御姐娇妻','小娇妻鹿蔓在丈夫明爷意外“失智”后，面对商场劲敌易卜拉希姆和公司内奸毛豆的联合欺压，仍对明爷不离不弃，后幸得青梅竹马李勤相助，加上自己勤学功夫，终于完美变身成职业女强人，肩负起照顾公司和家庭的责任。','//imgwx3.2345.com/dypcimg/img/c/66/sup198540_223x310.jpg',NULL,NULL,'84',4,1),
  (137,'美国队长3：内战','奥创事件之后，美国队长史蒂夫组建了一批新的英雄队伍继续维护正义。因为世界各国都想插手管理一系列事情，美国队长与钢铁侠对此理念不合，最终发生冲突。\r\n《美国队长3》是漫威电影宇宙第三阶段的第一部电影，同时也是漫威电影宇宙第13部电影，由安东尼・罗素、乔・罗素共同执导，克里斯・埃文斯、小罗伯特・唐尼主演，是一部科幻电影。影片定于2016年5月6日在美国上映（国内暂未定）。\r\n影片故事发生在《复仇者联盟2：奥创纪元》后，美国队长史蒂夫・罗杰斯在“奥创事件”之后，带领着一只新的超级英雄队伍继续维护世界和平，在此期间，各方面政治势力侵扰，引发了一系列矛盾，美国队长和钢铁侠之间更是因为政府注册法案的而产生','//imgwx5.2345.com/dypcimg/img/9/48/sup144639_223x310.jpg',NULL,NULL,'141',4,1),
  (138,'加勒比海盗5：死无对证','故事发生在《加勒比海盗3：世界的尽头》沉船湾之战20年后。 亡灵萨拉查船长（哈维尔・巴登 Javier Bardem 饰）率领自己的亡灵海军杀出了世界的尽头，“深海阎王”威尔・特纳（奥兰多・布鲁姆 Orlando Bloom 饰）乘飞翔的荷兰人号前来追杀却被其引入百慕大三角生死未卜。 获取自由的萨拉查肆虐加勒比海，征服了整个海域，即便是手刃了“黑胡子”爱德华・蒂奇（伊恩・麦柯肖恩 Ian McShane 饰）的海盗王赫克托・巴博萨船长（杰弗里・拉什 Geoffrey Rush 饰）也难以避免心爱的安妮女王复仇号沉入大海的惨剧。 幸存下来的巴博萨在女巫Haifaa Meni(格什菲・法拉哈尼 G','//imgwx2.2345.com/dypcimg/img/c/49/sup148575_223x310.jpg',NULL,NULL,'123',4,1),
  (139,'蚁人（英语）','行骗为生的斯科特・朗（保罗・路德 饰），在偷了汉克・皮姆博士（迈克尔・道格拉斯 饰）发明的蚁人战服后，有了自由收缩身体的超能力。汉克・皮姆博士开发出一种“皮姆粒子”，并和斯科特・朗一同成为“蚁人”，斯科特也不得不接受自己的英雄身份。','//imgwx3.2345.com/dypcimg/img/1/42/sup128968_223x310.jpg',NULL,NULL,'116',4,1),
  (140,'变形金刚1','霸天虎”的先遣部队旋风和毒蝎袭击了美军位于卡塔尔的军事基地，与此同时，路障帮助他的搭档迷乱潜入了美国总统的座机空中一号，通过电脑获悉，要想找到威震天就必须找到维特维奇家族的那副眼镜，上面有威震天关于能量块的信息扫描，而它现在的拥有者萨姆维特维奇（希安拉博夫饰）成为了“霸天虎”攻击的目标。萨姆是名高中生，学校里没有人相信他讲述的关于这副眼镜的历史，失望之余，萨姆把眼镜放到网上拍卖，然而从未引起买家的兴趣。这时父亲送了一辆破车给他，这辆车恰好就是“汽车人”大黄蜂的变形。大黄蜂帮助萨姆交上了漂亮的女朋友（梅甘福克斯饰），但很快就遭遇了“霸天虎”的袭击，擎天柱带着其他“汽车人”赶到，一场机器人大战由此','//imgwx3.2345.com/dypcimg/img/b/49/sup148695_223x310.jpg',NULL,NULL,'143',4,1),
  (141,'杀手没有假期','如果莱昂变成了专挑妇女和儿童下手的杀手，他肯定无法赢得那么多影迷的眼泪。说不定混迹于伦敦黑社会的雷(科林・法莱尔饰)就是这部经典的粉丝，所以在接到大佬哈里(拉尔夫・费因斯饰)的指示，要枪杀一名神父的时候，他显得十分的抵触和沮丧。但身为杀手界的顶级劳模，雷清楚完成工作要比个人感情重要，但是在杀死了神父之后，他立刻陷入了无限的忏悔和自责中。好在哈里派来暗中监视雷的老油条肯(布兰登・格里森饰)及时出手，将两眼发黑的雷拽上了车。听从哈里的命令，两人来到了比利时的文化名城布鲁日避风头，等待哈里的进一步指示。　　雷和肯住在布鲁日的同一间旅馆里，雷对此非常不满，一方面是没有从阴影中走出，一方面是他很讨厌这个','//imgwx5.2345.com/dypcimg/img/8/18/sup54705_223x310.jpg',NULL,NULL,'106',4,1),
  (142,'喜欢你（2017）','一场意外让平凡的厨师顾胜男（周冬雨 饰）结识了霸道总裁路晋（金城武 饰）。之后，路晋入住了顾胜男所在的酒店，在并不知晓对方真实身份的情况下，顾胜男用一道“女巫汤”征服了路晋挑剔的味蕾，一位厨师和一位食客，两人在美食的联结下产生了奇妙的缘分。 路晋的失眠症日益严重，一次偶然中，他发现自己竟然在顾胜男家的沙发上酣然入睡，从此，这张沙发便成为了他的专属床位。随着时间的推移，在一餐一饭之中，顾胜男和路晋之间渐渐产生了懵懂的感情。然而路晋在父亲严苛而又独裁的教育下长大，顾胜男则向来大大咧咧毛手毛脚，个性和身份迥异的他们压抑着内心的感情，由此生出了诸多的误会。','//imgwx3.2345.com/dypcimg/img/b/62/sup187565_223x310.jpg',NULL,NULL,'106',4,1),
  (143,'极盗车神','由英伦鬼才导演埃德加・赖特执导编剧的《极盗车神》，讲述了关于天赋极高的犯罪团伙司机――名为“Baby”的年轻人的故事。Baby专门负责帮银行抢劫犯开车逃脱。Baby因童年的一场事故导致耳疾，要靠专属的音乐来掌控开车节奏。偶然的一次邂逅，他与餐厅女服务生坠入爱河，并想就此金盆洗手。头目老大道哥表示，只要Baby再完成最后一次任务，就可以给他自由。殊不知，巨大的危险和挑战在等待着Baby','//imgwx4.2345.com/dypcimg/img/0/63/sup191303_223x310.jpg',NULL,NULL,'95',4,1),
  (144,'唐伯虎点秋香','剧情基本上仍是描述唐伯虎被秋香的三笑迷得失魂落魄，于是施计混入太师府当家奴，期望近水楼台追求秋香，可惜越弄越糟。后来唐伯虎的身份被揭穿，但他却适时以武功搭救了太夫人，因此获得点秋香的机会……','//imgwx2.2345.com/dypcimg/img/1/13/sup39567_223x310.jpg',NULL,NULL,'102',4,1),
  (145,'热血跨男','自古英雄难过美人关，不良少年中的王者张霄万万没想到自己会栽在一个女生的手里。 晓彤学习优秀，笑容甜美，跟平日里打架斗殴，顽劣不堪的少年张霄是俩个世界的人，张霄一直找机会跟晓彤告白，均以失败告终。万念俱灰之时，将晓彤的一句玩笑话信以为真，去挑战学校的跨栏王子刘星，结果俩人比试跨栏，张霄惨败的一塌糊涂。张霄开始努力的进行魔鬼训练，他的训练强度是刘星日常的两倍。体育生资格测试前夕，张霄的跟腱还在隐隐作痛。 测试当天，所有人都来为张霄打气，张霄到底是否能完成刘星都未曾掌握的技巧“七步栏”，他能否顺利的获得体育生资格与公主的爱心….','//imgwx2.2345.com/dypcimg/img/8/66/sup198535_223x310.jpg',NULL,NULL,'20',4,1),
  (146,'速度与激情1','洛杉矶的年轻人都热衷于街头赛车，在这里，只要你拥有速度，你就拥有一切。新晋警官布赖恩（保罗沃克 Paul Walker 饰）为了破获最近屡屡发生的飞车党劫车案而充当卧底，打入这里活跃的飞车党帮派中，搜集证据以期将罪犯乘之于法。布赖恩凭借高超的车技很快赢得了飞车党老大多米尼克（范迪塞尔 Vin Diesel 饰）的欣赏和信任，并且很快和多米尼克的妹妹米亚（乔丹娜布鲁斯特 Jordana Brewster 饰）堕入爱河。然而飞车党的第二把手文斯却是对布赖恩充满敌意，原来文斯一直暗恋米亚，而且布赖恩的到来威胁着他二把手的地位。\r\n这时，飞车党的党员们开始怀疑他们内部藏有奸细，布赖恩的日子开始不好过。','//imgwx3.2345.com/dypcimg/img/8/8/sup24646_223x310.jpg',NULL,NULL,'106',4,1),
  (147,'神秘巨星','少女伊西亚（塞伊拉・沃西 Zaira Wasim 饰）拥有着一副天生的好嗓子，对唱歌充满了热爱的她做梦都想成为一名歌星。然而，伊西亚生活在一个不自由的家庭之中，母亲娜吉玛（梅・维贾 Meher Vij 饰）常常遭到性格爆裂独断专横的父亲法鲁克（拉杰・阿晶 Raj Arjun 饰）的拳脚相向，伊西亚知道，想让父亲支持自己的音乐梦想是完全不可能的事情。 \r\n　　某日，母亲卖掉了金项链给伊西亚买了一台电脑，很快，伊西亚便发现，虽然无法再现实里实现梦想，但是网络中存在着更广阔的舞台。伊西亚录制了一段蒙着脸自弹自唱的视屏上传到了优兔网上，没想到收获了异常热烈的反响，著名音乐人夏克提（阿米尔・汗 Aami','//imgwx5.2345.com/dypcimg/img/c/65/sup196477_223x310.jpg',NULL,NULL,'20',4,1),
  (148,'决不撤退!','1950年11月，美军冲过三八线追击溃败北朝鲜军队的美军海军陆战队第一师和第七步兵师，在中朝边境进攻清津水库地区时，遭到中国人民志愿军优势兵力的猛烈反击，陷入中朝军队的重围之中。美军总部下令全面撤退。美军海军陆战队史密斯将军却公然违抗总部命令，下令部下反击中朝部队。海军陆战队斯提夫上尉率领一个营担任掩护任务，经过浴血激战，终于和主力部队一起冲出对方的包围圈。影评界认为这部影片是正面描写朝鲜战争双方的少有影片之一。它一方面以正面手法再现了中国人民志愿军入朝初期排山倒海的反击规模，同时也歌颂了美军海军陆战队顽强作战的斗志。影片中一个美军下士，刚满17岁，但是在一次夜战中身负三处枪伤的情况下坚守阵地','//imgwx4.2345.com/dypcimg/img/2/59/sup179814_223x310.jpg',NULL,NULL,'91',4,1),
  (149,'羞羞的铁拳','《羞羞的铁拳》主要讲述的是靠打假拳混日子的艾迪生，本来和正义感十足的体育记者马小是一对冤家，没想到因为一场意外的电击，男女身体互换。性别错乱后，两人互坑互害，引发了拳坛的大地震，也揭开了假拳界的秘密，惹来一堆麻烦，最终两人在“卷莲门”副掌门张茱萸的指点下，向恶势力挥起了羞羞的铁拳。','//imgwx2.2345.com/dypcimg/img/e/64/sup193458_223x310.jpg',NULL,NULL,'',4,1),
  (150,'茅山道士','　　阴十三的祖父在临终前传授与他一本“三清茅山术”，自此他开始苦心钻研。一次巧合，阴十三和胖子在游泳馆里碰见被怨灵缠身的穆然，在穆然就要溺水之际，阴十三咬破中指，以阳血逼退水中怨灵。经询问得知最近穆然身边总是发生可怕的灵异事件，几次险些丧命。在阴的再三追问下穆然道出不久前同好友蓝珊出游遭遇车祸，二人一同得救，但因时间紧迫救护人员先救了穆然，蓝珊最终去世。阴十三感觉与此事必有关系，他同胖子、穆然一起回到事发地点以柳叶开启阴阳眼见到了满是怨气的蓝珊。　　','//imgwx3.2345.com/dypcimg/img/4/52/sup158230_223x310.jpg',NULL,NULL,'64',4,1),
  (151,'死海','海洋生物学家被派去调查一个内陆咸水湖，已被归因于一个生物认为是传奇的海洋生命的神秘死亡。','//imgwx5.2345.com/dypcimg/img/b/50/sup150535_223x310.jpg',NULL,NULL,'90',4,1),
  (152,'飞虎队','老洪是飞虎队的大队长，带着副队长王强和队员彭亮一起，几人潜入了正泰国际洋行，暗杀了日本掌柜。岗村是日本特务队的队长，暗杀的消息激怒了他，使得他杀死了数十位无辜的脚夫以泄心头之恨。 　　老洪得知洋行新上任的掌柜山口旅团长正在途中，于是在政委的带领之下将其击毙，之后更杀死了岗村，替惨死的中国人民报仇了雪恨，同时大大打击了敌人嚣张的气焰。新任特务队长松尾人面兽心，他找来了特务秦雄使出奸计，企图将飞虎队队员一网打尽。不仅如此，松尾还绑架了老洪的妻子芳林嫂，企图利用她彻底将老洪消灭。','//imgwx1.2345.com/dypcimg/img/1/6/sup20941_223x310.jpg',NULL,NULL,'101',4,1),
  (153,'黄飞鸿之铁鸡斗蜈蚣','黄飞鸿也会做糗事：听信弟子梁宽（梁家仁）及牙擦苏（张卫健），他竟把宝芝林搬到了妓院旁边。但这令他的众弟子喜上眉梢。妓院老板黄狮虎（陈百祥）将黄飞鸿视作偶像，一心想拜他为师，却不被理会，其妹妹（袁咏仪）等妓女更是被他敬而远之。清廷命官雷一笑（徐忠信）实是义和团余孽，他勾结上级官员及能仁寺花和尚掳掠女性，准备将她们贩卖到南洋。黄飞鸿得知雷的阴谋后，开始想办法将被抢女性解救，被雷视作眼中钉。不久他同梁宽、牙擦苏等人表演舞狮时，因之前为救治病人拿自己当实验品服用西药，双耳突然失聪，被雷及其手下装扮的蜈蚣击败。回佛山医治路上，他遇到将他误会的铁燕（张敏）和其父亲，他们从四川来到广州找寻失踪的妹妹时，曾因','//imgwx2.2345.com/dypcimg/img/4/7/sup22389_223x310.jpg',NULL,NULL,'110',4,1),
  (154,'变形金刚5：最后的骑士','地球陷入毁灭危机，擎天柱失踪，越来越多的汽车人和霸天虎来到地球，前海豹突击队成员威廉・伦诺克斯（ 乔什・杜哈明饰）成为了政府军组建的TRF中的一员，负责绞杀地球上的汽车人――不论好坏。在芝加哥街头，伦诺克斯遇到了凯德・伊格尔（马克・沃尔伯格饰）、孤儿伊莎贝拉（伊莎贝拉・莫奈饰）。 凯德因为帮助支持汽车人而成为政府通缉犯，所以他在一个垃圾场看管着一群幸存的汽车人。与此同时，失踪的擎天柱被女巫昆塔莎黑化，要毁灭地球。拯救世界的责任于是落在了这支由凯德为首的非同寻常的队伍身上，大黄蜂、英国爵士（安东尼・霍普金斯饰）还有牛津大学教授（劳拉・哈德克饰），一场史诗浩劫彻底将地球变成了战场 。','//imgwx1.2345.com/dypcimg/img/0/56/sup168237_223x310.jpg',NULL,NULL,'20',4,1),
  (155,'笑傲江湖2：东方不败','令狐冲（李连杰饰）在按计划与日月神教的朋友会面后，将和小师妹岳灵珊（李嘉欣饰）等其他华山派师兄弟赴牛背山归隐退出江湖。路途中意外发现练就“葵花宝典”的东方不败（林青霞饰），但令狐冲不认识东方不败，误将对方当成一位美貌女子。后来几次相遇，还一道饮酒吟诗。在同任盈盈（关之琳饰）、蓝凤凰（袁洁莹饰）会合后，几个月来江湖不平，大事连连。任我行为东方不败所囚禁，他们商量着夺回日月神教，便乔装进入浪人营打探，救出任我行。另一边华山派弟子惨遭毒手，黑木崖上，令狐冲和一干人都想着报仇，他惊觉眼前人竟是东方不败。','//imgwx5.2345.com/dypcimg/img/e/20/sup61482_223x310.jpg',NULL,NULL,'108',4,1),
  (156,'自杀小队（X特遣队）','自杀小队”又称“X特遣队”，成员都是面对严重牢狱之灾的超级罪犯，包括死亡射手、鲨王、回旋镖队长和小丑女等，他们为美国政府干一些极其危险的脏活，以此来为自己减刑。每个成员脖子里都被装有纳米炸弹，如果不乖乖执行任务就会被炸死。目前，影片确定将由大卫・阿耶执导，并将于2016年上映。这意味着2016年大银幕上会有两部超级坏蛋电影――《自杀小队》和《险恶六人组》。','//imgwx4.2345.com/dypcimg/img/e/54/sup164302_223x310.jpg',NULL,NULL,'122',4,1),
  (157,'黄飞鸿之南北英雄','晚清时期，外来入侵，国家内忧外患，民不聊生。黄飞鸿面对列强的压迫，依旧弘扬中国文化，保护民族信仰，与外来者的打击坚持抗争。在这样一个社会动荡的时期，北拳宗师吴镇南，初入佛山北拳南传，却屡屡碰壁，面对黄飞鸿的盛情款待，吴镇南却不愿接受他的帮助。就在这个时候，洋人趁火打劫，把药品研究用在了吴镇南身上，导致两人矛盾再度激化，面对洋人的利用和威胁，吴镇南不得不和黄飞鸿展开激烈的一战。被洋人雇佣来协助研究项目的女孩路小月在秘密研究项目中也慢慢了解到洋人的残暴实验，渐渐地看清了洋人计划，危险一触即发。。。','//imgwx4.2345.com/dypcimg/img/6/66/sup198802_223x310.jpg',NULL,NULL,'103',4,1),
  (158,'蜘蛛侠','彼得是一个平凡的高中生，一次他被变种蜘蛛咬了之后便拥有了超能力。彼得好友哈利的父亲诺曼是一家生物科技公司的老板，一次意外吸入神经毒气后也有了超能力，引发了自身邪恶的本性。彼得化身的蜘蛛侠开始出击了！','//imgwx1.2345.com/dypcimg/img/5/17/sup52806_223x310.jpg',NULL,NULL,'121',4,1),
  (159,'加勒比海盗3：世界的尽头','被大章鱼吞下去的杰克船长进入了戴维・琼斯控制的“死界”――世界的尽头。为了拯救杰克，威尔・特纳、伊丽莎白和女巫等一行人也来到了“死界”。 找到杰克，他们一行人解开迷咒，逃出了世界尽头。而此时狡猾的卡特勒・贝凯特已经得到了戴维・琼斯的心脏，由此挟持戴维・琼斯，掌控所有的公海海盗，为东印度公司攫取利益。为了应对危难，各地海盗头目秘密集会，展开是否释放女海神，杀掉戴维・琼斯的争论。 原来女海神正是戴维・琼斯的旧情人，两人之间的微妙关系，成了双方交锋的关键。在平静的海面上，风雨降将至，最终的对决即将展开。每个人面临着怎样的命运？当最后的审判结束，大海能否重归往日的宁静？','//imgwx4.2345.com/dypcimg/img/6/20/sup61499_223x310.jpg',NULL,NULL,'169',4,1),
  (160,'007：大破天幕危机','数月前军情六处一块记载了潜伏于全世界恐怖组织内部所有特工名单信息的硬盘丢失，而追索硬盘的行动也以失败告终。在此之后，行动主要负责人M（朱迪・丹奇 Judi Dench 饰）被高层解雇，而军情六处也遭到惨烈的袭击，伤亡惨重。为了查到幕后真凶，M召来佯死的传奇特工詹姆士・邦德（丹尼尔・克雷格 Daniel Craig 饰）。经过一轮严苛的特训和测试，邦德重新服役。在伊芙（娜奥米・哈里斯 Naomie Harris 饰）和新搭档Q（本・卫肖 Ben Whishaw 饰）的协助下，邦德追寻着某前CIA特工的踪迹来到繁华大都会上海。在这一过程中，特工的名单不断泄露，而邦德最新一次的生死冒险也拉开序幕。','//imgwx5.2345.com/dypcimg/img/e/20/sup62548_223x310.jpg',NULL,NULL,'143',4,1),
  (161,'生死狙击','美国退役优秀狙击手鲍勃正在阿肯色州享受退役后的休闲时光，不料，平地生波，他的前上司前来拜访他，希望他能接受一项新的任务，在总统街头演讲那天充当隐形狙击手暗中保护总统。虽然十万分不情愿，但禁不住上司的游说，鲍勃最终答应了。总统演讲那天，鲍勃正在仔细观察着四周情况时，突然会场遭到枪手袭击，总统身边的衣索比亚主教被杀。鲍勃被当成暗杀者遭遇警方追捕。心知成了替罪羔羊，鲍勃在身中两枪的情况下奋力逃走了。鲍勃此时唯一的出路就是找出幕后的真凶，为自己洗刷罪名。','//imgwx5.2345.com/dypcimg/img/7/12/sup37516_223x310.jpg',NULL,NULL,'125',4,1),
  (162,'目标战','谁能保证明天战争不会到来？！\r\n面对中国周边日益严峻的国土安全形势，我们的百万雄狮厉兵秣马，严阵以待。本片描述的就是百万雄狮中的一支精锐力量！\r\n他们是王牌军中的“野狼”部队。他们战功卓着，所向披靡，却在一次现代化战争演习中遭遇到前所未有的惨败。在新任主官带领下，围绕目标中心战术，他们经历着“步兵特战化”的生死裂变。但是，在一场突发事件中，这支精锐却被特种部队“飞狐”再次击入谷底。军人的荣誉，男人的尊严，要求他们必须洗刷耻辱，击败特种兵。成功突围后，他们面临着一次新的现代化战争演习。潜伏，侦察，突击！每一个战士都成为战场最前沿的指挥官，他们通过卫星调控着导弹的发射、战机的轰炸、铁甲的奔袭。目标','//imgwx4.2345.com/dypcimg/img/e/31/sup94324_223x310.jpg',NULL,NULL,'93',4,1),
